class Dragon implements Enemy {
    private String name;
    private int health;
    private int firepower;
    private boolean canFly;

    public Dragon(String name, int firepower) {
        this.name = name;
        this.health = 200;
        this.firepower = firepower;
        this.canFly = true;
    }

    @Override
    public void attack() {
        if (firepower > 80) {
            System.out.println(name + " breathes devastating flames!");
        } else if (firepower > 50) {
            System.out.println(name + " breathes hot fire!");
        } else {
            System.out.println(name + " breathes weak flames!");
        }
    }

    @Override
    public void fly() {
        if (canFly && health > 50) {
            System.out.println(name + " soars majestically through the sky!");
        } else if (health <= 50) {
            System.out.println(name + " is too wounded to fly!");
        }
    }

    @Override
    public void poison() {
        throw new UnsupportedOperationException("Dragon doesn't use poison!");
    }

    @Override
    public void summonMinions() {
        throw new UnsupportedOperationException("Dragon doesn't summon minions!");
    }

    @Override
    public void takeDamage(int[] damageValues) {
        int totalDamage = 0;
        int criticalHits = 0;

        for (int i = 0; i < damageValues.length; i++) {
            if (damageValues[i] > 0) {
                int reducedDamage = Math.max(1, damageValues[i] - 5);
                totalDamage += reducedDamage;

                if (damageValues[i] > 30) {
                    criticalHits++;
                    System.out.println(name + " roars in pain from critical hit!");
                }
            }
        }

        health -= totalDamage;
        System.out.println(name + " takes " + totalDamage + " damage (" + criticalHits + " critical hits)! Health: " + health);

        if (health <= 0) {
            System.out.println(name + " crashes to the ground, defeated!");
            canFly = false;
        }
    }
}

class Spider implements Enemy {
    private String name;
    private int health;
    private boolean hasPoison;

    public Spider(String name) {
        this.name = name;
        this.health = 50;
        this.hasPoison = true;
    }

    @Override
    public void attack() {
        System.out.println(name + " bites with venomous fangs!");
    }

