        for (int i = 0; i < recipes.length; i++) {
            if (mealsCooked < 50) {
                if (recipes[i].equals("steak") && isHeadChef) {
                    System.out.println("Head Chef is preparing premium " + recipes[i]);
                    mealsCooked += 2;
                } else if (recipes[i].equals("pasta") || recipes[i].equals("pizza")) {
                    System.out.println("Chef is cooking " + recipes[i]);
                    mealsCooked++;
                } else {
                    System.out.println("Chef is preparing simple " + recipes[i]);
                }
            }
        }
