class Manager implements RestaurantWorker {
    private boolean conflict = true;
    private int teamSize = 15;
    private String[] departments = {"kitchen", "service", "cleaning", "security"};
    private boolean isBusy = false;

    public void cookFood() {
        System.out.println("Manager does not cook.");
    }

    public void serveCustomers() {
        System.out.println("Manager does not serve.");
    }

    public void cleanTables() {
        System.out.println("Manager does not clean.");
    }

    public void manageStaff() {
        for (String dept : departments) {
            if (conflict && dept.equals("kitchen")) {
                System.out.println("Manager resolving kitchen conflict");
            } else if (teamSize > 10 && dept.equals("service")) {
                for (int i = 1; i <= 3; i++) {
                    System.out.println("Manager scheduling service team shift " + i);
                }
            } else if (dept.equals("security") && !isBusy) {
                System.out.println("Manager checking security protocols");
            } else {
                System.out.println("Manager overseeing " + dept + " department");
            }
        }
