    @Override
    public boolean validatePayment(double amount) {
        return cardNumber != null && cardNumber.length() == 16 && amount > 0;
    }

    @Override
    public String getPaymentMethod() {
        return "Credit Card";
    }
}

public class BankTransferProcessor extends PaymentProcessor {
    private String accountNumber;
    private String routingNumber;

    public BankTransferProcessor(String accountNumber, String routingNumber) {
        this.accountNumber = accountNumber;
        this.routingNumber = routingNumber;
    }

    @Override
    public void processPayment(double amount) {
        if (amount < 100) {
            throw new IllegalArgumentException("Bank transfers must be at least $100");
        }
        if (!validatePayment(amount)) {
            throw new RuntimeException("Invalid bank transfer");
        }
        System.out.println("Processing bank transfer of $" + amount);
    }

    @Override
    public boolean validatePayment(double amount) {
        return accountNumber != null && routingNumber != null && amount >= 100;
    }

    @Override
    public String getPaymentMethod() {
        return "Bank Transfer";
    }
}

public class DigitalWalletProcessor extends PaymentProcessor {
    private String walletId;
    private double balance;

    public DigitalWalletProcessor(String walletId, double balance) {
        this.walletId = walletId;
        this.balance = balance;
    }

    @Override
    public void processPayment(double amount) {
        if (balance < amount) {
            throw new InsufficientFundsException("Insufficient wallet balance");
        }
        if (!validatePayment(amount)) {
            throw new RuntimeException("Invalid wallet payment");
        }
        balance -= amount;
        System.out.println("Processing wallet payment of $" + amount + ". Remaining balance: $" + balance);
    }

    @Override
    public boolean validatePayment(double amount) {
        return walletId != null && balance >= amount && amount > 0;
    }

    @Override
    public String getPaymentMethod() {
        return "Digital Wallet";
    }

    public double getBalance() {
        return balance;
    }
}

class InsufficientFundsException extends RuntimeException {
    public InsufficientFundsException(String message) {
        super(message);
    }
