from abc import ABC, abstractmethod
from decimal import Decimal
from datetime import datetime, timedelta
from typing import List, Dict, Any, Optional
import asyncio
import logging
from enum import Enum

class InstrumentType(Enum):
    BOND = "BOND"
    DERIVATIVE = "DERIVATIVE"
    EQUITY = "EQUITY"

class FinancialInstrument(ABC):
    def __init__(self, instrument_id: str, face_value: Decimal):
        self.instrument_id = instrument_id
        self.face_value = face_value
        self.creation_time = datetime.now()
        self.logger = logging.getLogger(self.__class__.__name__)

    @abstractmethod
    def calculate_current_value(self, as_of_date: datetime) -> Decimal:
        pass

    @abstractmethod
    async def execute_transaction(self, amount: Decimal, counterparty: str) -> bool:
        pass

    @abstractmethod
    def get_required_documents(self) -> List[str]:
        pass

    @abstractmethod
    def validate_instrument(self) -> bool:
        pass

    @abstractmethod
    def get_risk_metrics(self) -> Dict[str, Any]:
        pass

class CorporateBond(FinancialInstrument):
    def __init__(self, instrument_id: str, face_value: Decimal, coupon_rate: Decimal,
                 maturity_date: datetime, credit_rating: str):
        super().__init__(instrument_id, face_value)
        self.coupon_rate = coupon_rate
        self.maturity_date = maturity_date
        self.credit_rating = credit_rating
        self.coupon_payment_dates = self._generate_coupon_dates()

    def calculate_current_value(self, as_of_date: datetime) -> Decimal:
        if as_of_date > self.maturity_date:
            return Decimal('0')

        present_value = self.face_value
        for payment_date in self.coupon_payment_dates:
            if payment_date > as_of_date:
                coupon_payment = self.face_value * self.coupon_rate
                present_value += coupon_payment

        return present_value

    async def execute_transaction(self, amount: Decimal, counterparty: str) -> bool:
        if amount != self.face_value:
            raise ValueError("Corporate bonds must be traded at face value")

        if not self._validate_counterparty(counterparty):
            return False

        self.logger.info(f"Executing bond transaction: {self.instrument_id}")
        await asyncio.sleep(1)
        return True

    def get_required_documents(self) -> List[str]:
        return ["Bond Certificate", "Credit Rating Report", "Indenture Agreement"]

    def validate_instrument(self) -> bool:
        return (self.credit_rating and 
                self.maturity_date > datetime.now() and 
                self.coupon_rate > Decimal('0'))

    def get_risk_metrics(self) -> Dict[str, Any]:
        return {
            "credit_risk": self.credit_rating,
            "duration": self._calculate_duration(),
            "yield_to_maturity": self._calculate_ytm()
        }

    def _generate_coupon_dates(self) -> List[datetime]:
        dates = []
        current = self.creation_time + timedelta(days=180)
        while current < self.maturity_date:
            dates.append(current)
            current += timedelta(days=180)
        return dates

    def _validate_counterparty(self, counterparty: str) -> bool:
        return counterparty and len(counterparty) > 5

    def _calculate_duration(self) -> Decimal:
        return Decimal('2.5')

    def _calculate_ytm(self) -> Decimal:
        return self.coupon_rate * Decimal('1.1')

class DerivativeContract(FinancialInstrument):
    def __init__(self, instrument_id: str, notional_value: Decimal, underlying_asset: str,
                 expiration_date: datetime, strike_price: Decimal, contract_type: str):
        super().__init__(instrument_id, notional_value)
        self.underlying_asset = underlying_asset
        self.expiration_date = expiration_date
        self.strike_price = strike_price
        self.contract_type = contract_type
        self._is_marked_to_market = False

    def calculate_current_value(self, as_of_date: datetime) -> Decimal:
        if not self._is_marked_to_market:
            raise RuntimeError("Derivative must be marked to market before valuation")

        if as_of_date > self.expiration_date:
            return self._calculate_expiration_value()

        return self._calculate_time_value() + self._calculate_intrinsic_value()

    async def execute_transaction(self, amount: Decimal, counterparty: str) -> bool:
        if not self._is_marked_to_market:
            raise RuntimeError("Cannot trade unmarked derivative")

        if datetime.now() > self.expiration_date:
            raise RuntimeError("Cannot trade expired derivative")

        if amount <= Decimal('0'):
            raise ValueError("Transaction amount must be positive")

        self.logger.info(f"Executing derivative transaction: {self.instrument_id}")
        return await self._perform_complex_settlement(amount, counterparty)

    def get_required_documents(self) -> List[str]:
        if self.contract_type == "SWAP":
            return ["ISDA Master Agreement", "Confirmation", "Collateral Agreement", "Risk Disclosure"]
        return ["Derivative Confirmation", "Risk Disclosure", "Margin Agreement"]

    def validate_instrument(self) -> bool:
        base_validation = (self.underlying_asset and 
                          self.expiration_date > datetime.now() and 
                          self.strike_price > Decimal('0'))

        if self.contract_type == "SWAP" and not self._is_marked_to_market:
            return False

        return base_validation

    def get_risk_metrics(self) -> Dict[str, Any]:
        metrics = {
            "delta": self._calculate_delta(),
            "gamma": self._calculate_gamma(),
            "vega": self._calculate_vega(),
            "theta": self._calculate_theta(),
            "counterparty_risk": "HIGH"
        }

        if not self._is_marked_to_market:
            metrics["valuation_risk"] = "EXTREME"

        return metrics

    def mark_to_market(self):
        self._is_marked_to_market = True

    def _calculate_expiration_value(self) -> Decimal:
        return max(self.strike_price - self.face_value, Decimal('0'))

    def _calculate_time_value(self) -> Decimal:
        return Decimal('100')

    def _calculate_intrinsic_value(self) -> Decimal:
        return Decimal('50')

    async def _perform_complex_settlement(self, amount: Decimal, counterparty: str) -> bool:
        await asyncio.sleep(3)
        return True

    def _calculate_delta(self) -> Decimal:
        return Decimal('0.6')

    def _calculate_gamma(self) -> Decimal:
        return Decimal('0.05')

    def _calculate_vega(self) -> Decimal:
        return Decimal('0.3')

    def _calculate_theta(self) -> Decimal:
        return Decimal('-0.02')

