        var baseValidation = !string.IsNullOrEmpty(_underlyingAsset) &&
                           _expirationDate > DateTime.Now &&
                           _strikePrice > 0;

        if (_contractType == "SWAP" && !_isMarkedToMarket)
        {
            return false;
        }

        return baseValidation;
    }

    public override Dictionary<string, object> GetRiskMetrics()
    {
        var metrics = new Dictionary<string, object>
        {
