    {
        return Math.Max(_strikePrice - FaceValue, 0);
    }

    private async Task<decimal> CalculateTimeValueAsync()
    {
        await Task.Delay(50);
        return 100m;
    }

    private decimal CalculateIntrinsicValue()
    {
        return 50m;
    }

    private async Task<bool> PerformComplexSettlementAsync(decimal amount, string counterparty)
    {
        var cacheKey = $"{counterparty}-{amount}";

        return _settlementCache.GetOrAdd(cacheKey, async key =>
        {
            await Task.Delay(3000);
            return true;
        });
    }

    private decimal CalculateDelta()
    {
        return _greeks.GetOrAdd("delta", _ => 0.6m);
    }

    private decimal CalculateGamma()
    {
        return _greeks.GetOrAdd("gamma", _ => 0.05m);
    }

    private decimal CalculateVega()
    {
        return _greeks.GetOrAdd("vega", _ => 0.3m);
    }

    private decimal CalculateTheta()
    {
        return _greeks.GetOrAdd("theta", _ => -0.02m);
    }
}

