public class OrderProcessor {
    private FileLogger fileLogger;
    private DatabaseLogger databaseLogger;
    private CloudLogger cloudLogger;

    public OrderProcessor() {
        this.fileLogger = new FileLogger("/var/log/orders.log");
        this.databaseLogger = new DatabaseLogger("jdbc:mysql://localhost:3306/logs");
        this.cloudLogger = new CloudLogger("https://api.cloudlogs.com", "secret-key");
    }

    public void processOrder(String orderId, double amount) {
        try {
            fileLogger.log("INFO", "Processing order: " + orderId + " for amount: $" + amount);

            validateOrder(orderId, amount);

            databaseLogger.log("INFO", "Order validation successful for: " + orderId);

            chargePayment(amount);

            cloudLogger.log("INFO", "Payment processed successfully for order: " + orderId);

            updateInventory(orderId);

            fileLogger.log("SUCCESS", "Order completed: " + orderId);

        } catch (Exception e) {
            fileLogger.logError("Order processing failed for: " + orderId, e);
            databaseLogger.logError("Database error during order: " + orderId, e);
            cloudLogger.logError("Critical failure for order: " + orderId, e);
            throw e;
        }
    }

    private void validateOrder(String orderId, double amount) throws Exception {
        if (amount <= 0) {
            throw new Exception("Invalid order amount");
        }
        fileLogger.log("DEBUG", "Order validation passed for: " + orderId);
    }

    private void chargePayment(double amount) throws Exception {
        databaseLogger.log("INFO", "Attempting to charge payment: $" + amount);
        if (amount > 10000) {
            throw new Exception("Payment amount exceeds limit");
        }
    }

    private void updateInventory(String orderId) {
        cloudLogger.log("INFO", "Updating inventory for order: " + orderId);
    }
}

