            raise ValueError("Cannot charge negative amount")

        new_level = min(self.current_fuel_level + amount, self.fuel_capacity)
        self.current_fuel_level = new_level
        print(f"Charged {amount} kWh to {self.brand} battery")

    def get_remaining_range(self) -> float:
        return self.current_fuel_level * self.battery_efficiency

