            new_level = min(self.current_fuel_level + amount, self.fuel_capacity)
            self.current_fuel_level = new_level
            print(f"Added {amount} gallons of gasoline to {self.brand}")
        elif fuel_type == "electricity":
            print(f"Charging electric component of {self.brand}")
        else:
            raise ValueError(f"Hybrid vehicle cannot use {fuel_type}")

    def get_remaining_range(self) -> float:
        return (self.current_fuel_level * self.miles_per_gallon) + self.electric_range

