import kotlin.math.min

abstract class Vehicle(protected val brand: String, protected val fuelCapacity: Double) {
    protected var currentFuelLevel: Double = 0.0

    abstract fun refuel(fuelType: String, amount: Double)
    abstract fun getRemainingRange(): Double

    fun getBrand(): String = brand
    fun getFuelCapacity(): Double = fuelCapacity
    fun getCurrentFuelLevel(): Double = currentFuelLevel
}

class GasCar(brand: String, tankCapacity: Double, private val milesPerGallon: Double) : 
    Vehicle(brand, tankCapacity) {

    override fun refuel(fuelType: String, amount: Double) {
        if (fuelType !in listOf("gasoline", "premium", "regular")) {
            throw IllegalStateException("Gas vehicles cannot use $fuelType. Only gasoline types allowed.")
        }
        if (amount < 0) {
            throw IllegalArgumentException("Cannot refuel negative amount")
        }
        val newLevel = min(currentFuelLevel + amount, fuelCapacity)
        currentFuelLevel = newLevel
        println("Added $amount gallons of $fuelType to $brand")
    }

    override fun getRemainingRange(): Double {
        return currentFuelLevel * milesPerGallon
    }
}

class ElectricCar(brand: String, batteryCapacity: Double, private val batteryEfficiency: Double) :
    Vehicle(brand, batteryCapacity) {

    override fun refuel(fuelType: String, amount: Double) {
        if (fuelType != "electricity") {
            throw UnsupportedOperationException("Electric vehicles cannot be refueled with $fuelType. Use charging station instead.")
        }
        if (amount < 0) {
            throw IllegalArgumentException("Cannot charge negative amount")
        }
        val newLevel = min(currentFuelLevel + amount, fuelCapacity)
        currentFuelLevel = newLevel
        println("Charged $amount kWh to $brand battery")
    }

    override fun getRemainingRange(): Double {
        return currentFuelLevel * batteryEfficiency
    }

    fun chargeBattery(kWh: Double) {
        refuel("electricity", kWh)
    }
}

class HybridCar(brand: String, tankCapacity: Double, private val milesPerGallon: Double, 
                private val electricRange: Double) : Vehicle(brand, tankCapacity) {

    override fun refuel(fuelType: String, amount: Double) {
        when (fuelType) {
            in listOf("gasoline", "premium", "regular") -> {
                val newLevel = min(currentFuelLevel + amount, fuelCapacity)
                currentFuelLevel = newLevel
                println("Added $amount gallons of gasoline to $brand")
            }
            "electricity" -> {
                println("Charging electric component of $brand")
            }
            else -> {
                throw IllegalArgumentException("Hybrid vehicle cannot use $fuelType")
            }
        }
    }

    override fun getRemainingRange(): Double {
        return (currentFuelLevel * milesPerGallon) + electricRange
    }
