class StreamProcessor(
    processorId: String,
    configuration: Map<String, Any>
) : DataProcessor(processorId, configuration) {

    private val executorService = Executors.newFixedThreadPool(2)
    private val activeFiles = mutableListOf<Closeable>()

    init {
        supportedFormats.addAll(listOf("csv", "txt", "json", "xml", "log"))
    }

    override suspend fun processData(inputData: DataInput, options: ProcessingOptions): ProcessingResult {
        val startTime = System.currentTimeMillis()

        processingMutex.withLock {
            if (isProcessing) {
                throw IllegalStateException("Processor $processorId is already processing data")
            }
            setProcessing(true)
        }

        return try {
            validateInput(inputData)

            val inputPath = Paths.get(inputData.filePath)
            val outputPath = Paths.get(options.outputPath)

            withContext(Dispatchers.IO) {
                val inputReader = Files.newBufferedReader(inputPath).also { activeFiles.add(it) }
                val outputWriter = Files.newBufferedWriter(outputPath).also { activeFiles.add(it) }

                var lineCount = 0
                val batchBuffer = mutableListOf<String>()

                inputReader.useLines { lines ->
                    lines.forEach { line ->
                        val processedLine = processLine(line, options)
                        batchBuffer.add(processedLine)
                        lineCount++

                        if (batchBuffer.size >= options.batchSize) {
                            batchBuffer.forEach { outputWriter.write(it) }
                            batchBuffer.clear()
                        }

                        if (lineCount % 5000 == 0) {
                            println("Stream processor: $lineCount lines processed")
                        }
                    }
                }

                if (batchBuffer.isNotEmpty()) {
                    batchBuffer.forEach { outputWriter.write(it) }
                }

                outputWriter.flush()

                val processingTime = System.currentTimeMillis() - startTime
                ProcessingResult(
                    recordsProcessed = lineCount,
                    outputLocation = outputPath.toString(),
                    statusMessage = "STREAM_SUCCESS",
                    processingTimeMs = processingTime
                )
            }
        } catch (e: Exception) {
            val processingTime = System.currentTimeMillis() - startTime
            ProcessingResult(
                recordsProcessed = 0,
                outputLocation = null,
                statusMessage = "STREAM_ERROR: ${e.message}",
                processingTimeMs = processingTime,
                errors = listOf(e.message ?: "Unknown error")
            )
        } finally {
            setProcessing(false)
            cleanup()
        }
    }

    private fun processLine(line: String, options: ProcessingOptions): String {
        return if (options.transformData) {
            line.uppercase().trim() + "\n"
        } else {
            line.trim() + "\n"
        }
    }

    override fun validateInput(inputData: DataInput) {
        if (inputData.filePath.isBlank()) {
            throw ValidationException("Input file path cannot be empty")
        }

        val inputPath = Paths.get(inputData.filePath)
        if (!Files.exists(inputPath)) {
            throw ValidationException("Input file does not exist: ${inputData.filePath}")
        }

        if (!canProcess(inputData.formatType)) {
            throw ValidationException("Unsupported format for stream processing: ${inputData.formatType}")
        }

        val maxFileSize = (configuration["maxFileSize"] as? Long) ?: (100 * 1024 * 1024)
        if (Files.size(inputPath) > maxFileSize) {
            throw ValidationException("File too large for stream processing")
        }
    }

    override fun canProcess(formatType: String): Boolean {
        return supportedFormats.contains(formatType.lowercase())
    }

    override fun cleanup() {
        activeFiles.forEach { closeable ->
            try {
                closeable.close()
            } catch (e: Exception) {
                println("Cleanup error: ${e.message}")
            }
        }
        activeFiles.clear()
        executorService.shutdown()
    }
}

class DatabaseProcessor(
    processorId: String,
    configuration: Map<String, Any>
) : DataProcessor(processorId, configuration) {

    private var connection: Connection? = null
    private var currentStatement: Statement? = null

    init {
        supportedFormats.addAll(listOf("sql", "query", "dml"))
    }

    override suspend fun processData(inputData: DataInput, options: ProcessingOptions): ProcessingResult {
        throw UnsupportedOperationException(
            "DatabaseProcessor cannot use async processing due to transaction requirements. " +
            "Use processDatabaseSync() method instead for proper transaction handling."
        )
    }

    fun processDatabaseSync(inputData: DataInput, options: ProcessingOptions): ProcessingResult {
        val startTime = System.currentTimeMillis()

        runBlocking {
            processingMutex.withLock {
                if (isProcessing) {
                    throw IllegalStateException("Database processor $processorId is already processing")
                }
                setProcessing(true)
            }
        }

        return try {
            validateInput(inputData)
            establishConnection()

            val sqlContent = File(inputData.filePath).readText()
            val statements = sqlContent.split(';').map { it.trim() }.filter { it.isNotEmpty() }
            var executedStatements = 0

            connection?.autoCommit = false
            connection?.let { conn ->
                try {
                    statements.forEach { statement ->
                        currentStatement = conn.createStatement()
                        currentStatement?.execute(statement)
                        executedStatements++
                    }
                    conn.commit()

                    val processingTime = System.currentTimeMillis() - startTime
                    ProcessingResult(
                        recordsProcessed = executedStatements,
                        outputLocation = null,
                        statusMessage = "DATABASE_SUCCESS",
                        processingTimeMs = processingTime
                    )
                } catch (e: Exception) {
                    conn.rollback()
                    throw RuntimeException("Database transaction failed: ${e.message}")
                }
            } ?: throw RuntimeException("Failed to establish database connection")

        } catch (e: Exception) {
            val processingTime = System.currentTimeMillis() - startTime
            ProcessingResult(
                recordsProcessed = 0,
                outputLocation = null,
                statusMessage = "DATABASE_ERROR: ${e.message}",
                processingTimeMs = processingTime,
                errors = listOf(e.message ?: "Unknown error")
            )
        } finally {
            setProcessing(false)
            cleanup()
        }
    }

    private fun establishConnection() {
        val dbPath = configuration["dbPath"] as? String ?: ":memory:"
        connection = DriverManager.getConnection("jdbc:sqlite:$dbPath")
        connection?.autoCommit = true
    }

    override fun validateInput(inputData: DataInput) {
        if (inputData.filePath.isBlank()) {
            throw ValidationException("Database processor requires valid SQL file path")
        }

        if (!canProcess(inputData.formatType)) {
            throw ValidationException("Database processor cannot handle format: ${inputData.formatType}")
        }

        if (!configuration.containsKey("dbPath")) {
            throw ValidationException("Database processor requires database configuration")
        }

        val sqlPath = Paths.get(inputData.filePath)
        if (!Files.exists(sqlPath)) {
            throw ValidationException("SQL file not found: ${inputData.filePath}")
        }
    }

    override fun canProcess(formatType: String): Boolean {
        return supportedFormats.contains(formatType.lowercase())
    }

    override fun cleanup() {
        try {
            currentStatement?.close()
            connection?.close()
        } catch (e: Exception) {
            println("Database cleanup error: ${e.message}")
        } finally {
            currentStatement = null
            connection = null
        }
    }
}

