using System;
using System.Text.RegularExpressions;

public abstract class Document
{
    protected string content;
    protected string format;
    protected bool encrypted;

    public Document(string content, string format)
    {
        this.content = content;
        this.format = format;
        this.encrypted = false;
    }

    public abstract void Save();
    public abstract void Print();
    public abstract void Export(string targetFormat);
    public abstract void Compress();

    public string GetContent() => content;
    public string GetFormat() => format;
    public bool IsEncrypted() => encrypted;
}

public class WordDocument : Document
{
    private string author;
    private int wordCount;

    public WordDocument(string content, string author) : base(content, "DOCX")
    {
        this.author = author;
        this.wordCount = content.Split(' ').Length;
    }

    public override void Save()
    {
        Console.WriteLine($"Saving Word document with {wordCount} words");
    }

    public override void Print()
    {
        Console.WriteLine($"Printing Word document by {author}");
    }

    public override void Export(string targetFormat)
    {
        Console.WriteLine($"Exporting Word document to {targetFormat}");
    }

    public override void Compress()
    {
        Console.WriteLine("Compressing Word document");
        this.content = Regex.Replace(this.content, @"\s+", " ");
    }
}

public class SecureDocument : Document
{
    private string password;
    private bool isLocked;

    public SecureDocument(string content, string password) : base(content, "SECURE")
    {
        this.password = password;
        this.encrypted = true;
        this.isLocked = true;
    }

    public override void Save()
    {
        if (isLocked)
        {
            throw new UnauthorizedAccessException("Document is locked and cannot be saved");
        }
        Console.WriteLine("Saving secure document");
    }

    public override void Print()
    {
        if (isLocked)
        {
            throw new UnauthorizedAccessException("Document is locked and cannot be printed");
        }
        Console.WriteLine("Printing secure document");
    }

    public override void Export(string targetFormat)
    {
        throw new NotSupportedException("Secure documents cannot be exported for security reasons");
    }

    public override void Compress()
    {
        Console.WriteLine("Secure documents use built-in compression");
    }

    public void Unlock(string inputPassword)
    {
        if (password.Equals(inputPassword))
        {
            isLocked = false;
            Console.WriteLine("Document unlocked");
        }
        else
        {
            throw new UnauthorizedAccessException("Invalid password");
        }
    }
}

