import java.util.*;
import java.time.LocalDateTime;
import java.util.concurrent.locks.ReentrantLock;
import java.io.*;

public abstract class Document {
    protected String content;
    protected String format;
    protected boolean encrypted;
    protected LocalDateTime createdAt;
    protected LocalDateTime lastModified;
    protected Map<String, String> metadata;
    protected List<String> revisionHistory;
    protected DocumentPermissions permissions;

    public Document(String content, String format, DocumentPermissions permissions) {
        this.content = content;
        this.format = format;
        this.encrypted = false;
        this.createdAt = LocalDateTime.now();
        this.lastModified = LocalDateTime.now();
        this.metadata = new HashMap<>();
        this.revisionHistory = new ArrayList<>();
        this.permissions = permissions;
        this.revisionHistory.add("Document created at " + createdAt);
    }

    public abstract void save() throws DocumentException;
    public abstract void print() throws DocumentException;
    public abstract void export(String targetFormat) throws DocumentException;
    public abstract void compress() throws DocumentException;
    public abstract void backup(String location) throws DocumentException;
    public abstract DocumentStatistics getStatistics();
    public abstract void addWatermark(String watermarkText) throws DocumentException;
    public abstract void digitallySSign(String certificate) throws DocumentException;

    protected void updateLastModified() {
        this.lastModified = LocalDateTime.now();
        this.revisionHistory.add("Document modified at " + lastModified);
    }

    public String getContent() { return content; }
    public String getFormat() { return format; }
    public boolean isEncrypted() { return encrypted; }
    public DocumentPermissions getPermissions() { return permissions; }
}

class DocumentPermissions {
    private boolean canRead, canWrite, canPrint, canExport, canBackup;

    public DocumentPermissions(boolean canRead, boolean canWrite, boolean canPrint, boolean canExport, boolean canBackup) {
        this.canRead = canRead; this.canWrite = canWrite; this.canPrint = canPrint;
        this.canExport = canExport; this.canBackup = canBackup;
    }

    public boolean canRead() { return canRead; }
    public boolean canWrite() { return canWrite; }
    public boolean canPrint() { return canPrint; }
    public boolean canExport() { return canExport; }
    public boolean canBackup() { return canBackup; }
}

class DocumentStatistics {
    private int wordCount, characterCount, pageCount;
    private double fileSizeKB;

    public DocumentStatistics(int wordCount, int characterCount, int pageCount, double fileSizeKB) {
        this.wordCount = wordCount; this.characterCount = characterCount;
        this.pageCount = pageCount; this.fileSizeKB = fileSizeKB;
    }

    public int getWordCount() { return wordCount; }
    public int getCharacterCount() { return characterCount; }
    public int getPageCount() { return pageCount; }
    public double getFileSizeKB() { return fileSizeKB; }
}

class DocumentException extends Exception {
    public DocumentException(String message) { super(message); }
}

public class EnterpriseWordDocument extends Document {
    private String author;
    private String department;
    private List<String> collaborators;
    private ReentrantLock documentLock;
    private boolean isBeingEdited;

    public EnterpriseWordDocument(String content, String author, String department) {
        super(content, "DOCX", new DocumentPermissions(true, true, true, true, true));
        this.author = author;
        this.department = department;
        this.collaborators = new ArrayList<>();
        this.documentLock = new ReentrantLock();
        this.isBeingEdited = false;
        this.metadata.put("author", author);
        this.metadata.put("department", department);
    }

    public void save() throws DocumentException {
        documentLock.lock();
        try {
            if (!permissions.canWrite()) {
                throw new DocumentException("No write permission for this document");
            }
            System.out.println("Saving enterprise Word document by " + author + " in " + department);
            updateLastModified();
            isBeingEdited = false;
        } finally {
            documentLock.unlock();
        }
    }

    public void print() throws DocumentException {
        if (!permissions.canPrint()) {
            throw new DocumentException("No print permission for this document");
        }
        System.out.println("Printing enterprise Word document with watermark");
    }

    public void export(String targetFormat) throws DocumentException {
        if (!permissions.canExport()) {
            throw new DocumentException("No export permission for this document");
        }
        System.out.println("Exporting enterprise Word document to " + targetFormat + " with audit trail");
    }

    public void compress() throws DocumentException {
        System.out.println("Compressing enterprise Word document using advanced algorithms");
        this.content = this.content.replaceAll("\\s+", " ").trim();
        updateLastModified();
    }

    public void backup(String location) throws DocumentException {
        if (!permissions.canBackup()) {
            throw new DocumentException("No backup permission for this document");
        }
        System.out.println("Creating encrypted backup of enterprise document at " + location);
    }

    public DocumentStatistics getStatistics() {
        int wordCount = content.split("\\s+").length;
        int charCount = content.length();
        int pageCount = Math.max(1, wordCount / 250);
        double fileSize = content.length() * 0.001;
        return new DocumentStatistics(wordCount, charCount, pageCount, fileSize);
    }

    public void addWatermark(String watermarkText) throws DocumentException {
        System.out.println("Adding enterprise watermark: " + watermarkText);
        updateLastModified();
    }

    public void digitallySSign(String certificate) throws DocumentException {
        System.out.println("Digitally signing document with certificate: " + certificate);
        this.encrypted = true;
        updateLastModified();
    }
}

public class LegacyDocument extends Document {
    private String originalSystem;
    private boolean conversionRequired;
    private List<String> conversionErrors;

    public LegacyDocument(String content, String originalSystem) {
        super(content, "LEGACY", new DocumentPermissions(true, false, false, false, false));
        this.originalSystem = originalSystem;
        this.conversionRequired = true;
        this.conversionErrors = new ArrayList<>();
        this.metadata.put("originalSystem", originalSystem);
        this.metadata.put("migrationStatus", "pending");
    }

    public void save() throws DocumentException {
        throw new DocumentException("Legacy documents are read-only and cannot be saved in modern format");
    }

    public void print() throws DocumentException {
        throw new DocumentException("Legacy documents cannot be printed without conversion");
    }

    public void export(String targetFormat) throws DocumentException {
        throw new DocumentException("Legacy documents cannot be exported without manual conversion");
    }

    public void compress() throws DocumentException {
        throw new DocumentException("Legacy documents use proprietary compression that cannot be modified");
    }

    public void backup(String location) throws DocumentException {
        throw new DocumentException("Legacy documents require specialized backup procedures");
    }

    public DocumentStatistics getStatistics() {
        return new DocumentStatistics(0, content.length(), 1, content.length() * 0.002);
    }

    public void addWatermark(String watermarkText) throws DocumentException {
        throw new DocumentException("Cannot add watermark to legacy document format");
    }

    public void digitallySSign(String certificate) throws DocumentException {
        throw new DocumentException("Digital signatures not supported for legacy document format");
    }

    public void attemptConversion() {
        System.out.println("Attempting to convert legacy document from " + originalSystem);
        conversionErrors.add("Formatting may be lost during conversion");
        conversionErrors.add("Some features not supported in modern format");
    }
}

