from abc import ABC, abstractmethod

class Bird(ABC):
    def __init__(self, species, wing_span=0):
        self.species = species
        self.wing_span = wing_span

    @abstractmethod
    def fly(self):
        pass

    @abstractmethod
    def get_flight_speed(self):
        pass

    def get_species(self):
        return self.species

    def get_wing_span(self):
        return self.wing_span

class FlyingBird(Bird):
    def __init__(self, species, wing_span):
        super().__init__(species, wing_span)

    @abstractmethod
    def fly(self):
        pass

    @abstractmethod
    def get_flight_speed(self):
        pass

class Eagle(FlyingBird):
    def __init__(self, wing_span):
        super().__init__("Eagle", wing_span)

    def fly(self):
        print(f"Eagle soaring at high altitude with {self.wing_span}m wingspan")

    def get_flight_speed(self):
        return 80.0 + (self.wing_span * 10)

class Hummingbird(FlyingBird):
    def __init__(self, wing_span):
        super().__init__("Hummingbird", wing_span)

    def fly(self):
        print("Hummingbird hovering with rapid wing beats")

    def get_flight_speed(self):
        return 50.0

class NonflyingBird(Bird):
    def __init__(self, species):
        super().__init__(species)

    def fly(self):
        raise NotImplementedError("This bird cannot fly")

    def get_flight_speed(self):
        return 0.0

class Ostrich(NonflyingBird):
    def __init__(self, species, running_speed):
        super().__init__(species)
        self.running_speed = running_speed

    def run(self):
        print(f"{self.get_species()} running at {self.running_speed} km/h")
```
