public abstract class Bird
{
    protected string species;
    protected double wingSpan;

    public Bird(string species, double wingSpan)
    {
        this.species = species;
        this.wingSpan = wingSpan;
    }

    public abstract void Fly();
    public abstract double GetFlightSpeed();

    public string GetSpecies() => species;
    public double GetWingSpan() => wingSpan;
}

public class Eagle : Bird
{
    public Eagle(double wingSpan) : base("Eagle", wingSpan) { }

    public override void Fly()
    {
        Console.WriteLine($"Eagle soaring at high altitude with {wingSpan}m wingspan");
    }

    public override double GetFlightSpeed()
    {
        return 80.0 + (wingSpan * 10);
    }
}

public class Hummingbird : Bird
{
    public Hummingbird(double wingSpan) : base("Hummingbird", wingSpan) { }

    public override void Fly()
    {
        Console.WriteLine("Hummingbird hovering with rapid wing beats");
    }

    public override double GetFlightSpeed()
    {
        return 50.0;
    }
}

public class Ostrich : Bird
{
    private double runningSpeed;

    public Ostrich(double wingSpan, double runningSpeed) : base("Ostrich", wingSpan)
    {
        this.runningSpeed = runningSpeed;
    }

    public override void Fly()
    {
        throw new NotSupportedException("Ostriches are flightless birds!");
    }

    public override double GetFlightSpeed()
    {
        return 0.0;
    }

    public void Run()
    {
        Console.WriteLine($"Ostrich running at {runningSpeed} km/h");
    }
}

public class FlightSimulator
{
    public void SimulateFlight(Bird bird, double distance)
    {
        Console.WriteLine($"Starting flight simulation for {bird.GetSpecies()}");
        bird.Fly();
        double speed = bird.GetFlightSpeed();
        double time = speed > 0 ? distance / speed : 0;
        Console.WriteLine($"Flight time: {time} hours for {distance} km");
    }

    public void CompareFlightCapabilities(Bird[] birds, double testDistance)
    {
        foreach (var bird in birds)
        {
            SimulateFlight(bird, testDistance);
            Console.WriteLine("---");
        }
    }
}

