    def get_max_flight_speed(self) -> float:
        return 0.0

    def get_flight_efficiency(self) -> float:
        return 0.0

    def get_max_flight_duration(self) -> int:
        return 0

    def can_fly_at_altitude(self, altitude: int) -> bool:
