            throw UnsupportedOperationException("Electric vehicles cannot be refueled with $fuelType. Use charging station instead.")
        }
        if (amount < 0) {
            throw IllegalArgumentException("Cannot charge negative amount")
        }
        val newLevel = min(currentFuelLevel + amount, fuelCapacity)
        currentFuelLevel = newLevel
        println("Charged $amount kWh to $brand battery")
    }

