using System;
using System.Collections.Generic;

public abstract class Vehicle
{
    protected string brand;
    protected double fuelCapacity;
    protected double currentFuelLevel;

    public Vehicle(string brand, double fuelCapacity)
    {
        this.brand = brand;
        this.fuelCapacity = fuelCapacity;
        this.currentFuelLevel = 0;
    }

    public abstract void Refuel(string fuelType, double amount);
    public abstract double GetRemainingRange();

    public string Brand => brand;
    public double FuelCapacity => fuelCapacity;
    public double CurrentFuelLevel => currentFuelLevel;
}

public class GasCar : Vehicle
{
    private double milesPerGallon;

    public GasCar(string brand, double tankCapacity, double mpg) : base(brand, tankCapacity)
    {
        this.milesPerGallon = mpg;
    }

    public override void Refuel(string fuelType, double amount)
    {
        var acceptedFuels = new[] { "gasoline", "premium", "regular" };
        if (Array.IndexOf(acceptedFuels, fuelType) == -1)
        {
            throw new InvalidOperationException($"Gas vehicles cannot use {fuelType}. Only gasoline types allowed.");
        }
        if (amount < 0)
        {
            throw new ArgumentException("Cannot refuel negative amount");
        }
        double newLevel = Math.Min(currentFuelLevel + amount, fuelCapacity);
        this.currentFuelLevel = newLevel;
        Console.WriteLine($"Added {amount} gallons of {fuelType} to {brand}");
    }

    public override double GetRemainingRange()
    {
        return currentFuelLevel * milesPerGallon;
    }
}

public class ElectricCar : Vehicle
{
    private double batteryEfficiency;

    public ElectricCar(string brand, double batteryCapacity, double efficiency) : base(brand, batteryCapacity)
    {
        this.batteryEfficiency = efficiency;
    }

    public override void Refuel(string fuelType, double amount)
    {
        if (fuelType != "electricity")
        {
            throw new NotSupportedException($"Electric vehicles cannot be refueled with {fuelType}. Use charging station instead.");
        }
        if (amount < 0)
        {
            throw new ArgumentException("Cannot charge negative amount");
        }
        double newLevel = Math.Min(currentFuelLevel + amount, fuelCapacity);
        this.currentFuelLevel = newLevel;
        Console.WriteLine($"Charged {amount} kWh to {brand} battery");
    }

    public override double GetRemainingRange()
    {
        return currentFuelLevel * batteryEfficiency;
    }

    public void ChargeBattery(double kWh)
    {
        Refuel("electricity", kWh);
    }
}

public class HybridCar : Vehicle
{
    private double milesPerGallon;
    private double electricRange;

    public HybridCar(string brand, double tankCapacity, double mpg, double electricRange) : base(brand, tankCapacity)
    {
        this.milesPerGallon = mpg;
        this.electricRange = electricRange;
    }

    public override void Refuel(string fuelType, double amount)
    {
        var gasolineTypes = new[] { "gasoline", "premium", "regular" };
        if (Array.IndexOf(gasolineTypes, fuelType) != -1)
        {
            double newLevel = Math.Min(currentFuelLevel + amount, fuelCapacity);
            this.currentFuelLevel = newLevel;
            Console.WriteLine($"Added {amount} gallons of gasoline to {brand}");
        }
        else if (fuelType == "electricity")
        {
            Console.WriteLine($"Charging electric component of {brand}");
        }
        else
        {
            throw new ArgumentException($"Hybrid vehicle cannot use {fuelType}");
        }
    }

    public override double GetRemainingRange()
    {
        return (currentFuelLevel * milesPerGallon) + electricRange;
    }
}

public class FleetManager
{
    public void PerformMaintenanceRefuel(Vehicle[] fleet, string standardFuel, double standardAmount)
    {
        Console.WriteLine($"Starting fleet refueling with {standardFuel}");
        foreach (var vehicle in fleet)
        {
            try
            {
                vehicle.Refuel(standardFuel, standardAmount);
                Console.WriteLine($"{vehicle.Brand} range: {vehicle.GetRemainingRange()} miles");
            }
            catch (Exception e)
            {
                Console.WriteLine($"Failed to refuel {vehicle.Brand}: {e.Message}");
            }
        }
    }
}

