public abstract class Document {
    protected String content;
    protected String format;
    protected boolean encrypted;

    public Document(String content, String format) {
        this.content = content;
        this.format = format;
        this.encrypted = false;
    }

    public abstract void save();
    public abstract void print();
    public abstract void export(String targetFormat);
    public abstract void compress();

    public String getContent() { return content; }
    public String getFormat() { return format; }
    public boolean isEncrypted() { return encrypted; }
}

public class WordDocument extends Document {
    private String author;
    private int wordCount;

    public WordDocument(String content, String author) {
        super(content, "DOCX");
        this.author = author;
        this.wordCount = content.split(" ").length;
    }

    public void save() {
        System.out.println("Saving Word document with " + wordCount + " words");
    }

    public void print() {
        System.out.println("Printing Word document by " + author);
    }

    public void export(String targetFormat) {
        System.out.println("Exporting Word document to " + targetFormat);
    }

    public void compress() {
        System.out.println("Compressing Word document");
        this.content = this.content.replaceAll("\\s+", " ");
    }
}

public class SecureDocument extends Document {
    private String password;
    private boolean isLocked;

    public SecureDocument(String content, String password) {
        super(content, "SECURE");
        this.password = password;
        this.encrypted = true;
        this.isLocked = true;
    }

    public void save() {
        if (isLocked) {
            throw new SecurityException("Document is locked and cannot be saved");
        }
        System.out.println("Saving secure document");
    }

    public void print() {
        if (isLocked) {
            throw new SecurityException("Document is locked and cannot be printed");
        }
        System.out.println("Printing secure document");
    }

    public void export(String targetFormat) {
        throw new UnsupportedOperationException("Secure documents cannot be exported for security reasons");
    }

    public void compress() {
        System.out.println("Secure documents use built-in compression");
    }

    public void unlock(String inputPassword) {
        if (password.equals(inputPassword)) {
            isLocked = false;
            System.out.println("Document unlocked");
        } else {
            throw new SecurityException("Invalid password");
        }
    }
}

