import java.util.concurrent.Executors
import java.util.concurrent.Future
import java.util.concurrent.ExecutorService
import kotlin.math.min
import kotlin.math.PI

abstract class Bird(
    protected val species: String,
    protected val habitat: String,
    protected val wingSpan: Double,
    protected val weight: Double,
    protected val maxAltitude: Int,
    protected val isMigratory: Boolean
) {
    abstract fun performFlight(plan: FlightPlan): FlightResult
    abstract fun getMaxFlightSpeed(): Double
    abstract fun getFlightEfficiency(): Double
    abstract fun getMaxFlightDuration(): Int
    abstract fun canFlyAtAltitude(altitude: Int): Boolean

    fun getSpecies(): String = species
    fun getHabitat(): String = habitat
    fun getWingSpan(): Double = wingSpan
    fun getWeight(): Double = weight
    fun getMaxAltitude(): Int = maxAltitude
    fun getIsMigratory(): Boolean = isMigratory
}

data class FlightPlan(
    val distance: Double,
    val altitude: Int,
    val direction: String,
    val plannedDuration: Int
)

data class FlightResult(
    val successful: Boolean,
    val actualDistance: Double,
    val actualDuration: Int,
    val status: String
)

class Albatross : Bird(
    "Wandering Albatross", "Ocean", 3.5, 8.5, 15000, true
) {
    override fun performFlight(plan: FlightPlan): FlightResult {
        if (!canFlyAtAltitude(plan.altitude)) {
            return FlightResult(false, 0.0, 0, "Altitude too high for sustained flight")
        }

        val efficiency = getFlightEfficiency()
        val actualDistance = plan.distance * efficiency
        val actualDuration = (plan.plannedDuration / efficiency).toInt()

        return FlightResult(true, actualDistance, actualDuration, "Long-distance flight completed successfully")
    }

