    private List<Bird> flock;
    private SemaphoreSlim flightSemaphore;

    public AdvancedFlightManager()
    {
        this.flock = new List<Bird>();
        this.flightSemaphore = new SemaphoreSlim(10, 10);
    }

    public void AddBird(Bird bird)
    {
        flock.Add(bird);
    }

    public async Task ExecuteFormationFlightAsync(FlightPlan plan)
    {
        Console.WriteLine("=== Formation Flight Execution ===");
        Console.WriteLine($"Flight Plan: {plan.Distance}km at {plan.Altitude}m altitude");

        var tasks = new List<Task<FlightResult>>();

        foreach (var bird in flock)
        {
            var task = ExecuteBirdFlightAsync(bird, plan);
            tasks.Add(task);
        }

        for (int i = 0; i < tasks.Count; i++)
        {
