from abc import ABC, abstractmethod
import time

class PaymentProcessor(ABC):
    @abstractmethod
    def process_payment(self, amount):
        pass

    @abstractmethod
    def validate_payment(self, amount):
        pass

    @abstractmethod
    def get_payment_method(self):
        pass

class CreditCardProcessor(PaymentProcessor):
    def __init__(self, card_number, expiry_date):
        self.card_number = card_number
        self.expiry_date = expiry_date

    def process_payment(self, amount):
        if not self.validate_payment(amount):
            raise RuntimeError("Invalid credit card payment")
        print(f"Processing credit card payment of ${amount}")
        time.sleep(2)

    def validate_payment(self, amount):
        return self.card_number and len(self.card_number) == 16 and amount > 0

    def get_payment_method(self):
        return "Credit Card"

class BankTransferProcessor(PaymentProcessor):
    def __init__(self, account_number, routing_number):
        self.account_number = account_number
        self.routing_number = routing_number

    def process_payment(self, amount):
        if amount < 100:
            raise ValueError("Bank transfers must be at least $100")
        if not self.validate_payment(amount):
            raise RuntimeError("Invalid bank transfer")
        print(f"Processing bank transfer of ${amount}")

    def validate_payment(self, amount):
        return self.account_number and self.routing_number and amount >= 100

    def get_payment_method(self):
        return "Bank Transfer"

class DigitalWalletProcessor(PaymentProcessor):
    def __init__(self, wallet_id, balance):
        self.wallet_id = wallet_id
        self.balance = balance

    def process_payment(self, amount):
        if self.balance < amount:
            raise InsufficientFundsError("Insufficient wallet balance")
        if not self.validate_payment(amount):
            raise RuntimeError("Invalid wallet payment")
        self.balance -= amount
        print(f"Processing wallet payment of ${amount}. Remaining balance: ${self.balance}")

    def validate_payment(self, amount):
        return self.wallet_id and self.balance >= amount and amount > 0

    def get_payment_method(self):
        return "Digital Wallet"

    def get_balance(self):
        return self.balance

class InsufficientFundsError(Exception):
    pass

