import java.math.BigDecimal;
import java.time.LocalDateTime;
import java.util.*;
import java.util.concurrent.*;
import java.util.logging.Logger;

public abstract class FinancialInstrument {
    protected String instrumentId;
    protected BigDecimal faceValue;
    protected LocalDateTime creationTime;
    protected static final Logger logger = Logger.getLogger(FinancialInstrument.class.getName());

    public FinancialInstrument(String instrumentId, BigDecimal faceValue) {
        this.instrumentId = instrumentId;
        this.faceValue = faceValue;
        this.creationTime = LocalDateTime.now();
    }

    public abstract BigDecimal calculateCurrentValue(LocalDateTime asOfDate);
    public abstract CompletableFuture<Boolean> executeTransaction(BigDecimal amount, String counterparty);
    public abstract List<String> getRequiredDocuments();
    public abstract boolean validateInstrument();
    public abstract Map<String, Object> getRiskMetrics();

    public String getInstrumentId() { return instrumentId; }
    public BigDecimal getFaceValue() { return faceValue; }
    public LocalDateTime getCreationTime() { return creationTime; }
}

public class CorporateBond extends FinancialInstrument {
    private final BigDecimal couponRate;
    private final LocalDateTime maturityDate;
    private final String creditRating;
    private final List<LocalDateTime> couponPaymentDates;

    public CorporateBond(String instrumentId, BigDecimal faceValue, BigDecimal couponRate, 
                        LocalDateTime maturityDate, String creditRating) {
        super(instrumentId, faceValue);
        this.couponRate = couponRate;
        this.maturityDate = maturityDate;
        this.creditRating = creditRating;
        this.couponPaymentDates = generateCouponDates();
    }

    @Override
    public BigDecimal calculateCurrentValue(LocalDateTime asOfDate) {
        if (asOfDate.isAfter(maturityDate)) {
            return BigDecimal.ZERO;
        }
        BigDecimal presentValue = faceValue;
        for (LocalDateTime paymentDate : couponPaymentDates) {
            if (paymentDate.isAfter(asOfDate)) {
                BigDecimal couponPayment = faceValue.multiply(couponRate);
                presentValue = presentValue.add(couponPayment);
            }
        }
        return presentValue;
    }

    @Override
    public CompletableFuture<Boolean> executeTransaction(BigDecimal amount, String counterparty) {
        return CompletableFuture.supplyAsync(() -> {
            if (amount.compareTo(faceValue) != 0) {
                throw new IllegalArgumentException("Corporate bonds must be traded at face value");
            }
            if (!validateCounterparty(counterparty)) {
                return false;
            }
            logger.info("Executing bond transaction: " + instrumentId);
            return true;
        });
    }

    @Override
    public List<String> getRequiredDocuments() {
        return Arrays.asList("Bond Certificate", "Credit Rating Report", "Indenture Agreement");
    }

    @Override
    public boolean validateInstrument() {
        return creditRating != null && !creditRating.isEmpty() && 
               maturityDate.isAfter(LocalDateTime.now()) && 
               couponRate.compareTo(BigDecimal.ZERO) > 0;
    }

    @Override
    public Map<String, Object> getRiskMetrics() {
        Map<String, Object> metrics = new HashMap<>();
        metrics.put("creditRisk", creditRating);
        metrics.put("duration", calculateDuration());
        metrics.put("yieldToMaturity", calculateYTM());
        return metrics;
    }

    private List<LocalDateTime> generateCouponDates() {
        List<LocalDateTime> dates = new ArrayList<>();
        LocalDateTime current = creationTime.plusMonths(6);
        while (current.isBefore(maturityDate)) {
            dates.add(current);
            current = current.plusMonths(6);
        }
        return dates;
    }

    private boolean validateCounterparty(String counterparty) {
        return counterparty != null && counterparty.length() > 5;
    }

    private BigDecimal calculateDuration() {
        return BigDecimal.valueOf(2.5);
    }

    private BigDecimal calculateYTM() {
        return couponRate.multiply(BigDecimal.valueOf(1.1));
    }
}

public class DerivativeContract extends FinancialInstrument {
    private final String underlyingAsset;
    private final LocalDateTime expirationDate;
    private final BigDecimal strikePrice;
    private final String contractType;
    private volatile boolean isMarkedToMarket = false;

    public DerivativeContract(String instrumentId, BigDecimal notionalValue, String underlyingAsset,
                             LocalDateTime expirationDate, BigDecimal strikePrice, String contractType) {
        super(instrumentId, notionalValue);
        this.underlyingAsset = underlyingAsset;
        this.expirationDate = expirationDate;
        this.strikePrice = strikePrice;
        this.contractType = contractType;
    }

    @Override
    public BigDecimal calculateCurrentValue(LocalDateTime asOfDate) {
        if (!isMarkedToMarket) {
            throw new IllegalStateException("Derivative must be marked to market before valuation");
        }
        if (asOfDate.isAfter(expirationDate)) {
            return calculateExpirationValue();
        }
        return calculateTimeValue().add(calculateIntrinsicValue());
    }

    @Override
    public CompletableFuture<Boolean> executeTransaction(BigDecimal amount, String counterparty) {
        return CompletableFuture.supplyAsync(() -> {
            if (!isMarkedToMarket) {
                throw new IllegalStateException("Cannot trade unmarked derivative");
            }
            if (LocalDateTime.now().isAfter(expirationDate)) {
                throw new IllegalStateException("Cannot trade expired derivative");
            }
            if (amount.compareTo(BigDecimal.ZERO) <= 0) {
                throw new IllegalArgumentException("Transaction amount must be positive");
            }
            logger.info("Executing derivative transaction: " + instrumentId);
            return performComplexSettlement(amount, counterparty);
        });
    }

    @Override
    public List<String> getRequiredDocuments() {
        if ("SWAP".equals(contractType)) {
            return Arrays.asList("ISDA Master Agreement", "Confirmation", "Collateral Agreement", "Risk Disclosure");
        }
        return Arrays.asList("Derivative Confirmation", "Risk Disclosure", "Margin Agreement");
    }

    @Override
    public boolean validateInstrument() {
        boolean baseValidation = underlyingAsset != null && !underlyingAsset.isEmpty() &&
                               expirationDate.isAfter(LocalDateTime.now()) &&
                               strikePrice.compareTo(BigDecimal.ZERO) > 0;

