    {
        return _greeks.GetOrAdd("gamma", _ => 0.05m);
    }

    private decimal CalculateVega()
    {
        return _greeks.GetOrAdd("vega", _ => 0.3m);
    }

    private decimal CalculateTheta()
    {
        return _greeks.GetOrAdd("theta", _ => -0.02m);
    }
}

