import kotlin.math.min

abstract class Vehicle(protected val brand: String, protected val fuelCapacity: Double) {
    protected var currentFuelLevel: Double = 0.0

    abstract fun refuel(fuelType: String, amount: Double)
    abstract fun getRemainingRange(): Double

    fun getBrand(): String = brand
    fun getFuelCapacity(): Double = fuelCapacity
    fun getCurrentFuelLevel(): Double = currentFuelLevel
}

class GasCar(brand: String, tankCapacity: Double, private val milesPerGallon: Double) : 
    Vehicle(brand, tankCapacity) {

    override fun refuel(fuelType: String, amount: Double) {
        if (fuelType !in listOf("gasoline", "premium", "regular")) {
            throw IllegalStateException("Gas vehicles cannot use $fuelType. Only gasoline types allowed.")
        }
        if (amount < 0) {
            throw IllegalArgumentException("Cannot refuel negative amount")
        }
