    {
        try
        {
            currentCommand?.Dispose();
            connection?.Close();
            connection?.Dispose();
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Database cleanup error: {ex.Message}");
        }
        finally
        {
            currentCommand = null;
            connection = null;
        }
    }
