        return 0.0
    }

    override fun getFlightEfficiency(): Double {
        return 0.0
    }

    override fun getMaxFlightDuration(): Int {
        return 0
    }

    override fun canFlyAtAltitude(altitude: Int): Boolean {
        return false
    }

    fun getSwimSpeed(): Double = swimSpeed

    fun swim(distance: Double): String {
        val time = distance / swimSpeed
        return "Swimming %.2f km in %.2f hours".format(distance, time)
    }
}

