        println("=== Formation Flight Execution ===")
        println("Flight Plan: ${plan.distance}km at ${plan.altitude}m altitude")

        val futures = mutableListOf<Future<FlightResult>>()

        for (bird in flock) {
            val future = flightExecutor.submit<FlightResult> {
