abstract class PaymentProcessor {
    abstract suspend fun processPayment(amount: Double)
    abstract fun validatePayment(amount: Double): Boolean
    abstract fun getPaymentMethod(): String
}

class CreditCardProcessor(private val cardNumber: String, private val expiryDate: String) : PaymentProcessor() {
    override suspend fun processPayment(amount: Double) {
