        }
        if (!validatePayment(amount)) {
            throw RuntimeException("Invalid bank transfer")
        }
        println("Processing bank transfer of $$amount")
    }

    override fun validatePayment(amount: Double): Boolean {
        return accountNumber.isNotEmpty() && routingNumber.isNotEmpty() && amount >= 100
    }

    override fun getPaymentMethod(): String = "Bank Transfer"
}

