from abc import ABC, abstractmethod
import re

class Document(ABC):
    def __init__(self, content, format_type):
        self.content = content
        self.format = format_type
        self.encrypted = False

    @abstractmethod
    def save(self):
        pass

    @abstractmethod
    def print_doc(self):
        pass

    @abstractmethod
    def export(self, target_format):
        pass

    @abstractmethod
    def compress(self):
        pass

    def get_content(self):
        return self.content

    def get_format(self):
        return self.format

    def is_encrypted(self):
        return self.encrypted

class WordDocument(Document):
    def __init__(self, content, author):
        super().__init__(content, "DOCX")
        self.author = author
        self.word_count = len(content.split())

    def save(self):
        print(f"Saving Word document with {self.word_count} words")

    def print_doc(self):
        print(f"Printing Word document by {self.author}")

    def export(self, target_format):
        print(f"Exporting Word document to {target_format}")

    def compress(self):
        print("Compressing Word document")
        self.content = re.sub(r'\s+', ' ', self.content)

class SecureDocument(Document):
    def __init__(self, content, password):
        super().__init__(content, "SECURE")
        self.password = password
        self.encrypted = True
        self.is_locked = True

    def save(self):
        if self.is_locked:
            raise PermissionError("Document is locked and cannot be saved")
        print("Saving secure document")

    def print_doc(self):
        if self.is_locked:
            raise PermissionError("Document is locked and cannot be printed")
        print("Printing secure document")

    def export(self, target_format):
        raise NotImplementedError("Secure documents cannot be exported for security reasons")

    def compress(self):
        print("Secure documents use built-in compression")

    def unlock(self, input_password):
        if self.password == input_password:
            self.is_locked = False
            print("Document unlocked")
        else:
            raise PermissionError("Invalid password")

