import kotlin.collections.mutableMapOf

data class DocumentPermissions(
    val canRead: Boolean,
    val canWrite: Boolean,
    val canPrint: Boolean,
    val canExport: Boolean,
    val canBackup: Boolean
)

data class DocumentStatistics(
    val wordCount: Int,
    val characterCount: Int,
    val pageCount: Int,
    val fileSizeKB: Double
)

class DocumentException(message: String) : Exception(message)

abstract class Document(
    protected var content: String,
    protected val format: String,
    protected val permissions: DocumentPermissions
) {
    protected var encrypted: Boolean = false
    protected val createdAt: LocalDateTime = LocalDateTime.now()
    protected var lastModified: LocalDateTime = LocalDateTime.now()
    protected val metadata: MutableMap<String, String> = mutableMapOf()
    protected val revisionHistory: MutableList<String> = mutableListOf()

    init {
        revisionHistory.add("Document created at $createdAt")
    }

    abstract fun save()
    abstract fun print()
    abstract fun export(targetFormat: String)
    abstract fun compress()
    abstract fun backup(location: String)
    abstract fun getStatistics(): DocumentStatistics
    abstract fun addWatermark(watermarkText: String)
    abstract fun digitallySign(certificate: String)

    protected fun updateLastModified() {
        lastModified = LocalDateTime.now()
        revisionHistory.add("Document modified at $lastModified")
    }

    fun getContent(): String = content
    fun getFormat(): String = format
    fun isEncrypted(): Boolean = encrypted
    fun getPermissions(): DocumentPermissions = permissions
}

