using System;
using System.Collections.Generic;
using System.Threading.Tasks;
using System.Threading;

public abstract class Bird
{
    protected string species;
    protected string habitat;
    protected double wingSpan;
    protected double weight;
    protected int maxAltitude;
    protected bool isMigratory;

    public Bird(string species, string habitat, double wingSpan, double weight, int maxAltitude, bool isMigratory)
    {
        this.species = species;
        this.habitat = habitat;
        this.wingSpan = wingSpan;
        this.weight = weight;
        this.maxAltitude = maxAltitude;
        this.isMigratory = isMigratory;
    }

    public abstract FlightResult PerformFlight(FlightPlan plan);
    public abstract double GetMaxFlightSpeed();
    public abstract double GetFlightEfficiency();
    public abstract int GetMaxFlightDuration();
    public abstract bool CanFlyAtAltitude(int altitude);

    public string Species => species;
    public string Habitat => habitat;
    public double WingSpan => wingSpan;
    public double Weight => weight;
    public int MaxAltitude => maxAltitude;
    public bool IsMigratory => isMigratory;
}

public class FlightPlan
{
    public double Distance { get; private set; }
    public int Altitude { get; private set; }
    public string Direction { get; private set; }
    public int PlannedDuration { get; private set; }

    public FlightPlan(double distance, int altitude, string direction, int plannedDuration)
    {
        Distance = distance;
        Altitude = altitude;
        Direction = direction;
        PlannedDuration = plannedDuration;
    }
}

public class FlightResult
{
    public bool Successful { get; private set; }
    public double ActualDistance { get; private set; }
    public int ActualDuration { get; private set; }
    public string Status { get; private set; }

    public FlightResult(bool successful, double actualDistance, int actualDuration, string status)
    {
        Successful = successful;
        ActualDistance = actualDistance;
        ActualDuration = actualDuration;
        Status = status;
    }
}

public class Albatross : Bird
{
    public Albatross() : base("Wandering Albatross", "Ocean", 3.5, 8.5, 15000, true)
    {
    }

    public override FlightResult PerformFlight(FlightPlan plan)
    {
        if (!CanFlyAtAltitude(plan.Altitude))
        {
            return new FlightResult(false, 0, 0, "Altitude too high for sustained flight");
        }

        double efficiency = GetFlightEfficiency();
        double actualDistance = plan.Distance * efficiency;
        int actualDuration = (int)(plan.PlannedDuration / efficiency);

        return new FlightResult(true, actualDistance, actualDuration, "Long-distance flight completed successfully");
    }

    public override double GetMaxFlightSpeed() => 120.0;

    public override double GetFlightEfficiency() => 1.2;

    public override int GetMaxFlightDuration() => 1440;

    public override bool CanFlyAtAltitude(int altitude) => altitude <= maxAltitude;
}

public class Falcon : Bird
{
    public Falcon() : base("Peregrine Falcon", "Cliffs", 1.2, 1.5, 20000, false)
    {
    }

    public override FlightResult PerformFlight(FlightPlan plan)
    {
        if (!CanFlyAtAltitude(plan.Altitude))
        {
            return new FlightResult(false, 0, 0, "Altitude exceeds maximum capability");
        }

        double speed = GetMaxFlightSpeed();
        double actualDistance = Math.Min(plan.Distance, speed * (plan.PlannedDuration / 60.0));
        int actualDuration = (int)(actualDistance / speed * 60);

        return new FlightResult(true, actualDistance, actualDuration, "High-speed flight completed");
    }

    public override double GetMaxFlightSpeed() => 300.0;

    public override double GetFlightEfficiency() => 0.8;

    public override int GetMaxFlightDuration() => 180;

    public override bool CanFlyAtAltitude(int altitude) => altitude <= maxAltitude;
}

public class Emperor : Bird
{
    private double swimSpeed;

    public Emperor() : base("Emperor Penguin", "Antarctica", 0.8, 30.0, 0, false)
    {
        this.swimSpeed = 15.0;
    }

    public override FlightResult PerformFlight(FlightPlan plan)
    {
        throw new NotSupportedException("Emperor penguins are flightless! They can only swim and walk.");
    }

    public override double GetMaxFlightSpeed()
    {
        return 0.0;
    }

    public override double GetFlightEfficiency()
    {
        return 0.0;
    }

    public override int GetMaxFlightDuration()
    {
        return 0;
    }

    public override bool CanFlyAtAltitude(int altitude)
    {
        return false;
    }

    public double SwimSpeed => swimSpeed;

    public string Swim(double distance)
    {
        double time = distance / swimSpeed;
        return $"Swimming {distance:F2} km in {time:F2} hours";
    }
}

public class AdvancedFlightManager
{
    private List<Bird> flock;
    private SemaphoreSlim flightSemaphore;

    public AdvancedFlightManager()
    {
        this.flock = new List<Bird>();
        this.flightSemaphore = new SemaphoreSlim(10, 10);
    }

    public void AddBird(Bird bird)
    {
        flock.Add(bird);
    }

    public async Task ExecuteFormationFlightAsync(FlightPlan plan)
    {
        Console.WriteLine("=== Formation Flight Execution ===");
        Console.WriteLine($"Flight Plan: {plan.Distance}km at {plan.Altitude}m altitude");

        var tasks = new List<Task<FlightResult>>();

        foreach (var bird in flock)
        {
            var task = ExecuteBirdFlightAsync(bird, plan);
            tasks.Add(task);
        }

        for (int i = 0; i < tasks.Count; i++)
        {
            try
            {
                var result = await tasks[i];
                var bird = flock[i];
                Console.WriteLine($"{bird.Species}: {result.Status}");
                if (result.Successful)
                {
                    Console.WriteLine($"  Distance: {result.ActualDistance}km, Duration: {result.ActualDuration}min");
                }
            }
            catch (Exception e)
            {
                Console.WriteLine($"{flock[i].Species}: Flight failed - {e.Message}");
            }
            Console.WriteLine("---");
        }
    }

    private async Task<FlightResult> ExecuteBirdFlightAsync(Bird bird, FlightPlan plan)
    {
        await flightSemaphore.WaitAsync();
        try
        {
            Console.WriteLine($"Starting flight for {bird.Species}");
            return await Task.Run(() => bird.PerformFlight(plan));
        }
        finally
        {
            flightSemaphore.Release();
        }
    }

    public void PerformFlightCapabilityAnalysis()
    {
        Console.WriteLine("=== Flight Capability Analysis ===");
        foreach (var bird in flock)
        {
            Console.WriteLine($"{bird.Species}:");
            Console.WriteLine($"  Max Speed: {bird.GetMaxFlightSpeed()} km/h");
            Console.WriteLine($"  Efficiency: {bird.GetFlightEfficiency()}");
            Console.WriteLine($"  Max Duration: {bird.GetMaxFlightDuration()} minutes");
            Console.WriteLine($"  Max Altitude: {bird.MaxAltitude}m");
            Console.WriteLine();
        }
    }

    public void Dispose()
    {
        flightSemaphore?.Dispose();
    }
}

