from abc import ABC, abstractmethod
from typing import List

class Vehicle(ABC):
    def __init__(self, brand: str, fuel_capacity: float):
        self.brand = brand
        self.fuel_capacity = fuel_capacity
        self.current_fuel_level = 0.0

    @abstractmethod
    def refuel(self, fuel_type: str, amount: float) -> None:
        pass

    @abstractmethod
    def get_remaining_range(self) -> float:
        pass

    @property
    def fuel_level(self) -> float:
        return self.current_fuel_level

class GasCar(Vehicle):
    def __init__(self, brand: str, tank_capacity: float, mpg: float):
        super().__init__(brand, tank_capacity)
        self.miles_per_gallon = mpg

    def refuel(self, fuel_type: str, amount: float) -> None:
        if fuel_type not in ["gasoline", "premium", "regular"]:
            raise ValueError(f"Gas vehicles cannot use {fuel_type}. Only gasoline types allowed.")
        if amount < 0:
            raise ValueError("Cannot refuel negative amount")

        new_level = min(self.current_fuel_level + amount, self.fuel_capacity)
        self.current_fuel_level = new_level
        print(f"Added {amount} gallons of {fuel_type} to {self.brand}")

    def get_remaining_range(self) -> float:
        return self.current_fuel_level * self.miles_per_gallon

class ElectricCar(Vehicle):
    def __init__(self, brand: str, battery_capacity: float, efficiency: float):
        super().__init__(brand, battery_capacity)
        self.battery_efficiency = efficiency

    def refuel(self, fuel_type: str, amount: float) -> None:
        if fuel_type != "electricity":
            raise NotImplementedError(f"Electric vehicles cannot be refueled with {fuel_type}. Use charging station instead.")
        if amount < 0:
            raise ValueError("Cannot charge negative amount")

        new_level = min(self.current_fuel_level + amount, self.fuel_capacity)
        self.current_fuel_level = new_level
        print(f"Charged {amount} kWh to {self.brand} battery")

    def get_remaining_range(self) -> float:
        return self.current_fuel_level * self.battery_efficiency

    def charge_battery(self, kwh: float) -> None:
        self.refuel("electricity", kwh)

class HybridCar(Vehicle):
    def __init__(self, brand: str, tank_capacity: float, mpg: float, electric_range: float):
        super().__init__(brand, tank_capacity)
        self.miles_per_gallon = mpg
        self.electric_range = electric_range

    def refuel(self, fuel_type: str, amount: float) -> None:
        if fuel_type in ["gasoline", "premium", "regular"]:
            new_level = min(self.current_fuel_level + amount, self.fuel_capacity)
            self.current_fuel_level = new_level
            print(f"Added {amount} gallons of gasoline to {self.brand}")
        elif fuel_type == "electricity":
            print(f"Charging electric component of {self.brand}")
        else:
            raise ValueError(f"Hybrid vehicle cannot use {fuel_type}")

    def get_remaining_range(self) -> float:
        return (self.current_fuel_level * self.miles_per_gallon) + self.electric_range

class FleetManager:
    def perform_maintenance_refuel(self, fleet: List[Vehicle], standard_fuel: str, standard_amount: float) -> None:
        print(f"Starting fleet refueling with {standard_fuel}")
        for vehicle in fleet:
            try:
                vehicle.refuel(standard_fuel, standard_amount)
                print(f"{vehicle.brand} range: {vehicle.get_remaining_range()} miles")
            except Exception as e:
                print(f"Failed to refuel {vehicle.brand}: {str(e)}")

