            self._establish_connection()
            self.cleanup_handler = DatabaseCleanupHandler(self._connection, None)  # Update cleanup handler with connection

            with open(input_data.file_path, 'r') as sql_file:
                sql_content = sql_file.read()

            statements = [stmt.strip() for stmt in sql_content.split(';') if stmt.strip()]
            executed_statements = 0

            self._connection.execute('BEGIN TRANSACTION')

            try:
                for statement in statements:
                    self._current_cursor = self._connection.cursor()
                    self._current_cursor.execute(statement)
                    executed_statements += 1

                self._connection.commit()

                processing_time = time.time() - start_time
                return ProcessingResult(
                    records_processed=executed_statements,
                    output_location=None,
                    status_message="DATABASE_SUCCESS",
                    processing_time=processing_time
                )

            except Exception as e:
                self._connection.rollback()
                raise RuntimeError(f"Database transaction failed: {str(e)}")

        except Exception as e:
            processing_time = time.time() - start_time
            return ProcessingResult(
                records_processed=0,
                output_location=None,
