import java.time.LocalDateTime
import java.util.concurrent.locks.ReentrantLock
import kotlin.collections.mutableListOf
import kotlin.collections.mutableMapOf

data class DocumentPermissions(
    val canRead: Boolean,
    val canWrite: Boolean,
    val canPrint: Boolean,
    val canExport: Boolean,
    val canBackup: Boolean
)

data class DocumentStatistics(
    val wordCount: Int,
    val characterCount: Int,
    val pageCount: Int,
    val fileSizeKB: Double
)

class DocumentException(message: String) : Exception(message)

abstract class Document(
    protected var content: String,
    protected val format: String,
    protected val permissions: DocumentPermissions
) {
    protected var encrypted: Boolean = false
    protected val createdAt: LocalDateTime = LocalDateTime.now()
    protected var lastModified: LocalDateTime = LocalDateTime.now()
    protected val metadata: MutableMap<String, String> = mutableMapOf()
    protected val revisionHistory: MutableList<String> = mutableListOf()

    init {
        revisionHistory.add("Document created at $createdAt")
    }

    abstract fun save()
    abstract fun print()
    abstract fun export(targetFormat: String)
    abstract fun compress()
    abstract fun backup(location: String)
    abstract fun getStatistics(): DocumentStatistics
    abstract fun addWatermark(watermarkText: String)
    abstract fun digitallySign(certificate: String)

    protected fun updateLastModified() {
        lastModified = LocalDateTime.now()
        revisionHistory.add("Document modified at $lastModified")
    }

    fun getContent(): String = content
    fun getFormat(): String = format
    fun isEncrypted(): Boolean = encrypted
    fun getPermissions(): DocumentPermissions = permissions
}

class EnterpriseWordDocument(
    content: String,
    private val author: String,
    private val department: String
) : Document(content, "DOCX", DocumentPermissions(true, true, true, true, true)) {

    private val collaborators: MutableList<String> = mutableListOf()
    private val documentLock = ReentrantLock()
    private var isBeingEdited: Boolean = false

    init {
        metadata["author"] = author
        metadata["department"] = department
    }

    override fun save() {
        documentLock.lock()
        try {
            if (!permissions.canWrite) {
                throw DocumentException("No write permission for this document")
            }
            println("Saving enterprise Word document by $author in $department")
            updateLastModified()
            isBeingEdited = false
        } finally {
            documentLock.unlock()
        }
    }

    override fun print() {
        if (!permissions.canPrint) {
            throw DocumentException("No print permission for this document")
        }
        println("Printing enterprise Word document with watermark")
    }

    override fun export(targetFormat: String) {
        if (!permissions.canExport) {
            throw DocumentException("No export permission for this document")
        }
        println("Exporting enterprise Word document to $targetFormat with audit trail")
    }

    override fun compress() {
        println("Compressing enterprise Word document using advanced algorithms")
        content = content.replace(Regex("\\s+"), " ").trim()
        updateLastModified()
    }

    override fun backup(location: String) {
        if (!permissions.canBackup) {
            throw DocumentException("No backup permission for this document")
        }
        println("Creating encrypted backup of enterprise document at $location")
    }

    override fun getStatistics(): DocumentStatistics {
        val wordCount = content.split(Regex("\\s+")).size
        val charCount = content.length
        val pageCount = maxOf(1, wordCount / 250)
        val fileSize = content.length * 0.001
        return DocumentStatistics(wordCount, charCount, pageCount, fileSize)
    }

    override fun addWatermark(watermarkText: String) {
        println("Adding enterprise watermark: $watermarkText")
        updateLastModified()
    }

    override fun digitallySign(certificate: String) {
        println("Digitally signing document with certificate: $certificate")
        encrypted = true
        updateLastModified()
    }

    fun addCollaborator(collaborator: String) {
        collaborators.add(collaborator)
        updateLastModified()
    }

    fun startEditing() {
        documentLock.lock()
        try {
            isBeingEdited = true
            println("Document is now being edited by $author")
        } finally {
            documentLock.unlock()
        }
    }
}

class LegacyDocument(
    content: String,
    private val originalSystem: String
) : Document(content, "LEGACY", DocumentPermissions(true, false, false, false, false)) {

    private val conversionRequired: Boolean = true
    private val conversionErrors: MutableList<String> = mutableListOf()

    init {
        metadata["originalSystem"] = originalSystem
        metadata["migrationStatus"] = "pending"
    }

    override fun save() {
        throw DocumentException("Legacy documents are read-only and cannot be saved in modern format")
    }

    override fun print() {
        throw DocumentException("Legacy documents cannot be printed without conversion")
    }

    override fun export(targetFormat: String) {
        throw DocumentException("Legacy documents cannot be exported without manual conversion")
    }

    override fun compress() {
        throw DocumentException("Legacy documents use proprietary compression that cannot be modified")
    }

    override fun backup(location: String) {
        throw DocumentException("Legacy documents require specialized backup procedures")
    }

    override fun getStatistics(): DocumentStatistics {
        return DocumentStatistics(0, content.length, 1, content.length * 0.002)
    }

    override fun addWatermark(watermarkText: String) {
        throw DocumentException("Cannot add watermark to legacy document format")
    }

    override fun digitallySign(certificate: String) {
        throw DocumentException("Digital signatures not supported for legacy document format")
    }

    fun attemptConversion() {
        println("Attempting to convert legacy document from $originalSystem")
        conversionErrors.add("Formatting may be lost during conversion")
        conversionErrors.add("Some features not supported in modern format")
    }

    fun getConversionErrors(): List<String> = conversionErrors.toList()
    fun isConversionRequired(): Boolean = conversionRequired
}

