        this.habitat = habitat;
        this.wingSpan = wingSpan;
        this.weight = weight;
        this.maxAltitude = maxAltitude;
        this.isMigratory = isMigratory;
    }

    public abstract FlightResult performFlight(FlightPlan plan);
    public abstract double getMaxFlightSpeed();
    public abstract double getFlightEfficiency();
    public abstract int getMaxFlightDuration();
    public abstract boolean canFlyAtAltitude(int altitude);

    public String getSpecies() { return species; }
    public String getHabitat() { return habitat; }
    public double getWingSpan() { return wingSpan; }
    public double getWeight() { return weight; }
    public int getMaxAltitude() { return maxAltitude; }
    public boolean isMigratory() { return isMigratory; }
}

public class FlightPlan {
    private double distance;
    private int altitude;
    private String direction;
    private int plannedDuration;

    public FlightPlan(double distance, int altitude, String direction, int plannedDuration) {
        this.distance = distance;
        this.altitude = altitude;
        this.direction = direction;
        this.plannedDuration = plannedDuration;
    }

    public double getDistance() { return distance; }
    public int getAltitude() { return altitude; }
    public String getDirection() { return direction; }
    public int getPlannedDuration() { return plannedDuration; }
}

public class FlightResult {
    private boolean successful;
    private double actualDistance;
    private int actualDuration;
    private String status;

    public FlightResult(boolean successful, double actualDistance, int actualDuration, String status) {
        this.successful = successful;
        this.actualDistance = actualDistance;
        this.actualDuration = actualDuration;
        this.status = status;
    }

    public boolean isSuccessful() { return successful; }
    public double getActualDistance() { return actualDistance; }
    public int getActualDuration() { return actualDuration; }
    public String getStatus() { return status; }
}

public class Albatross extends Bird {
    public Albatross() {
        super("Wandering Albatross", "Ocean", 3.5, 8.5, 15000, true);
    }

    @Override
    public FlightResult performFlight(FlightPlan plan) {
        if (!canFlyAtAltitude(plan.getAltitude())) {
            return new FlightResult(false, 0, 0, "Altitude too high for sustained flight");
        }

        double efficiency = getFlightEfficiency();
        double actualDistance = plan.getDistance() * efficiency;
        int actualDuration = (int) (plan.getPlannedDuration() / efficiency);

        return new FlightResult(true, actualDistance, actualDuration, "Long-distance flight completed successfully");
    }

    @Override
    public double getMaxFlightSpeed() { return 120.0; }

    @Override
    public double getFlightEfficiency() { return 1.2; }

    @Override
    public int getMaxFlightDuration() { return 1440; }

    @Override
    public boolean canFlyAtAltitude(int altitude) { return altitude <= maxAltitude; }
}

public class Falcon extends Bird {
    public Falcon() {
        super("Peregrine Falcon", "Cliffs", 1.2, 1.5, 20000, false);
    }

    @Override
    public FlightResult performFlight(FlightPlan plan) {
        if (!canFlyAtAltitude(plan.getAltitude())) {
            return new FlightResult(false, 0, 0, "Altitude exceeds maximum capability");
        }

        double speed = getMaxFlightSpeed();
        double actualDistance = Math.min(plan.getDistance(), speed * (plan.getPlannedDuration() / 60.0));
        int actualDuration = (int) (actualDistance / speed * 60);

        return new FlightResult(true, actualDistance, actualDuration, "High-speed flight completed");
    }

    @Override
    public double getMaxFlightSpeed() { return 300.0; }

    @Override
    public double getFlightEfficiency() { return 0.8; }

    @Override
    public int getMaxFlightDuration() { return 180; }

    @Override
    public boolean canFlyAtAltitude(int altitude) { return altitude <= maxAltitude; }
}

public class Emperor extends Bird {
    private double swimSpeed;

    public Emperor() {
        super("Emperor Penguin", "Antarctica", 0.8, 30.0, 0, false);
        this.swimSpeed = 15.0;
    }

    @Override
    public FlightResult performFlight(FlightPlan plan) {
        throw new UnsupportedOperationException("Emperor penguins are flightless! They can only swim and walk.");
    }

    @Override
    public double getMaxFlightSpeed() {
        return 0.0;
    }

    @Override
    public double getFlightEfficiency() {
        return 0.0;
    }

    @Override
    public int getMaxFlightDuration() {
        return 0;
    }

    @Override
    public boolean canFlyAtAltitude(int altitude) {
        return false;
    }

    public double getSwimSpeed() { return swimSpeed; }

    public String swim(double distance) {
        double time = distance / swimSpeed;
        return String.format("Swimming %.2f km in %.2f hours", distance, time);
    }
}

public class AdvancedFlightManager {
    private List<Bird> flock;
    private ExecutorService flightExecutor;

    public AdvancedFlightManager() {
        this.flock = new ArrayList<>();
        this.flightExecutor = Executors.newFixedThreadPool(10);
    }

    public void addBird(Bird bird) {
        flock.add(bird);
    }

    public void executeFormationFlight(FlightPlan plan) {
        System.out.println("=== Formation Flight Execution ===");
        System.out.println("Flight Plan: " + plan.getDistance() + "km at " + plan.getAltitude() + "m altitude");

        List<Future<FlightResult>> futures = new ArrayList<>();

        for (Bird bird : flock) {
            Future<FlightResult> future = flightExecutor.submit(() -> {
                System.out.println("Starting flight for " + bird.getSpecies());
                return bird.performFlight(plan);
            });
            futures.add(future);
        }

        for (int i = 0; i < futures.size(); i++) {
            try {
                FlightResult result = futures.get(i).get();
                Bird bird = flock.get(i);
                System.out.println(bird.getSpecies() + ": " + result.getStatus());
                if (result.isSuccessful()) {
                    System.out.println("  Distance: " + result.getActualDistance() + "km, Duration: " + result.getActualDuration() + "min");
                }
            } catch (Exception e) {
                System.out.println(flock.get(i).getSpecies() + ": Flight failed - " + e.getMessage());
            }
            System.out.println("---");
        }
    }

    public void performFlightCapabilityAnalysis() {
        System.out.println("=== Flight Capability Analysis ===");
        for (Bird bird : flock) {
            System.out.println(bird.getSpecies() + ":");
            System.out.println("  Max Speed: " + bird.getMaxFlightSpeed() + " km/h");
            System.out.println("  Efficiency: " + bird.getFlightEfficiency());
            System.out.println("  Max Duration: " + bird.getMaxFlightDuration() + " minutes");
            System.out.println("  Max Altitude: " + bird.getMaxAltitude() + "m");
            System.out.println();
        }
    }

    public void shutdown() {
        flightExecutor.shutdown();
    }
}

