import java.util.concurrent.Executors
import java.util.concurrent.Future
import java.util.concurrent.ExecutorService
import kotlin.math.min
import kotlin.math.PI

abstract class Bird(
    protected val species: String,
    protected val habitat: String,
    protected val wingSpan: Double,
    protected val weight: Double,
    protected val maxAltitude: Int,
    protected val isMigratory: Boolean
) {
    abstract fun performFlight(plan: FlightPlan): FlightResult
    abstract fun getMaxFlightSpeed(): Double
    abstract fun getFlightEfficiency(): Double
    abstract fun getMaxFlightDuration(): Int
    abstract fun canFlyAtAltitude(altitude: Int): Boolean

    fun getSpecies(): String = species
    fun getHabitat(): String = habitat
    fun getWingSpan(): Double = wingSpan
    fun getWeight(): Double = weight
    fun getMaxAltitude(): Int = maxAltitude
    fun getIsMigratory(): Boolean = isMigratory
}

data class FlightPlan(
    val distance: Double,
    val altitude: Int,
    val direction: String,
    val plannedDuration: Int
)

data class FlightResult(
    val successful: Boolean,
    val actualDistance: Double,
    val actualDuration: Int,
    val status: String
)

class Albatross : Bird(
    "Wandering Albatross", "Ocean", 3.5, 8.5, 15000, true
) {
    override fun performFlight(plan: FlightPlan): FlightResult {
        if (!canFlyAtAltitude(plan.altitude)) {
            return FlightResult(false, 0.0, 0, "Altitude too high for sustained flight")
        }

        val efficiency = getFlightEfficiency()
        val actualDistance = plan.distance * efficiency
        val actualDuration = (plan.plannedDuration / efficiency).toInt()

        return FlightResult(true, actualDistance, actualDuration, "Long-distance flight completed successfully")
    }

    override fun getMaxFlightSpeed(): Double = 120.0

    override fun getFlightEfficiency(): Double = 1.2

    override fun getMaxFlightDuration(): Int = 1440

    override fun canFlyAtAltitude(altitude: Int): Boolean = altitude <= maxAltitude
}

class Falcon : Bird(
    "Peregrine Falcon", "Cliffs", 1.2, 1.5, 20000, false
) {
    override fun performFlight(plan: FlightPlan): FlightResult {
        if (!canFlyAtAltitude(plan.altitude)) {
            return FlightResult(false, 0.0, 0, "Altitude exceeds maximum capability")
        }

        val speed = getMaxFlightSpeed()
        val actualDistance = min(plan.distance, speed * (plan.plannedDuration / 60.0))
        val actualDuration = (actualDistance / speed * 60).toInt()

        return FlightResult(true, actualDistance, actualDuration, "High-speed flight completed")
    }

    override fun getMaxFlightSpeed(): Double = 300.0

    override fun getFlightEfficiency(): Double = 0.8

    override fun getMaxFlightDuration(): Int = 180

    override fun canFlyAtAltitude(altitude: Int): Boolean = altitude <= maxAltitude
}

class Emperor : Bird(
    "Emperor Penguin", "Antarctica", 0.8, 30.0, 0, false
) {
    private val swimSpeed: Double = 15.0

    override fun performFlight(plan: FlightPlan): FlightResult {
        throw UnsupportedOperationException("Emperor penguins are flightless! They can only swim and walk.")
    }

    override fun getMaxFlightSpeed(): Double {
        return 0.0
    }

    override fun getFlightEfficiency(): Double {
        return 0.0
    }

    override fun getMaxFlightDuration(): Int {
        return 0
    }

    override fun canFlyAtAltitude(altitude: Int): Boolean {
        return false
    }

    fun getSwimSpeed(): Double = swimSpeed

    fun swim(distance: Double): String {
        val time = distance / swimSpeed
        return "Swimming %.2f km in %.2f hours".format(distance, time)
    }
}

class AdvancedFlightManager {
    private val flock: MutableList<Bird> = mutableListOf()
    private val flightExecutor: ExecutorService = Executors.newFixedThreadPool(10)

    fun addBird(bird: Bird) {
        flock.add(bird)
    }

    fun executeFormationFlight(plan: FlightPlan) {
        println("=== Formation Flight Execution ===")
        println("Flight Plan: ${plan.distance}km at ${plan.altitude}m altitude")

        val futures = mutableListOf<Future<FlightResult>>()

        for (bird in flock) {
            val future = flightExecutor.submit<FlightResult> {
                println("Starting flight for ${bird.getSpecies()}")
                bird.performFlight(plan)
            }
            futures.add(future)
        }

        for (i in futures.indices) {
            try {
                val result = futures[i].get()
                val bird = flock[i]
                println("${bird.getSpecies()}: ${result.status}")
                if (result.successful) {
                    println("  Distance: ${result.actualDistance}km, Duration: ${result.actualDuration}min")
                }
            } catch (e: Exception) {
                println("${flock[i].getSpecies()}: Flight failed - ${e.message}")
            }
            println("---")
        }
    }

    fun performFlightCapabilityAnalysis() {
        println("=== Flight Capability Analysis ===")
        for (bird in flock) {
            println("${bird.getSpecies()}:")
            println("  Max Speed: ${bird.getMaxFlightSpeed()} km/h")
            println("  Efficiency: ${bird.getFlightEfficiency()}")
            println("  Max Duration: ${bird.getMaxFlightDuration()} minutes")
            println("  Max Altitude: ${bird.getMaxAltitude()}m")
            println()
        }
    }

    fun shutdown() {
        flightExecutor.shutdown()
    }
}

