import kotlinx.coroutines.delay

abstract class PaymentProcessor {
    abstract suspend fun processPayment(amount: Double)
    abstract fun validatePayment(amount: Double): Boolean
    abstract fun getPaymentMethod(): String
}

class CreditCardProcessor(private val cardNumber: String, private val expiryDate: String) : PaymentProcessor() {
    override suspend fun processPayment(amount: Double) {
        if (!validatePayment(amount)) {
            throw RuntimeException("Invalid credit card payment")
        }
        println("Processing credit card payment of $$amount")
        delay(2000)
    }

    override fun validatePayment(amount: Double): Boolean {
        return cardNumber.isNotEmpty() && cardNumber.length == 16 && amount > 0
    }

    override fun getPaymentMethod(): String = "Credit Card"
}

class BankTransferProcessor(private val accountNumber: String, private val routingNumber: String) : PaymentProcessor() {
    override suspend fun processPayment(amount: Double) {
        if (amount < 100) {
            throw IllegalArgumentException("Bank transfers must be at least $100")
        }
        if (!validatePayment(amount)) {
            throw RuntimeException("Invalid bank transfer")
        }
        println("Processing bank transfer of $$amount")
    }

    override fun validatePayment(amount: Double): Boolean {
        return accountNumber.isNotEmpty() && routingNumber.isNotEmpty() && amount >= 100
    }

    override fun getPaymentMethod(): String = "Bank Transfer"
}

class DigitalWalletProcessor(private val walletId: String, private var balance: Double) : PaymentProcessor() {
    override suspend fun processPayment(amount: Double) {
        if (balance < amount) {
            throw InsufficientFundsException("Insufficient wallet balance")
        }
        if (!validatePayment(amount)) {
            throw RuntimeException("Invalid wallet payment")
        }
        balance -= amount
        println("Processing wallet payment of $$amount. Remaining balance: $$balance")
    }

    override fun validatePayment(amount: Double): Boolean {
        return walletId.isNotEmpty() && balance >= amount && amount > 0
    }

    override fun getPaymentMethod(): String = "Digital Wallet"

