abstract class Bird(val species: String, val wingSpan: Double) {
    abstract fun fly()
    abstract fun getFlightSpeed(): Double

    fun getSpecies(): String = species
    fun getWingSpan(): Double = wingSpan
}

class Eagle(wingSpan: Double) : Bird("Eagle", wingSpan) {
    override fun fly() {
        println("Eagle soaring at high altitude with ${wingSpan}m wingspan")
    }

    override fun getFlightSpeed(): Double {
        return 80.0 + (wingSpan * 10)
    }
}

class Hummingbird(wingSpan: Double) : Bird("Hummingbird", wingSpan) {
    override fun fly() {
        println("Hummingbird hovering with rapid wing beats")
    }

    override fun getFlightSpeed(): Double {
        return 50.0
    }
}

class Ostrich(wingSpan: Double, private val runningSpeed: Double) : Bird("Ostrich", wingSpan) {
    override fun fly() {
        throw UnsupportedOperationException("Ostriches are flightless birds!")
    }

    override fun getFlightSpeed(): Double {
        return 0.0
    }

    fun run() {
        println("Ostrich running at $runningSpeed km/h")
    }
}

class FlightSimulator {
    fun simulateFlight(bird: Bird, distance: Double) {
        println("Starting flight simulation for ${bird.getSpecies()}")
        bird.fly()
        val speed = bird.getFlightSpeed()
        val time = if (speed > 0) distance / speed else 0.0
        println("Flight time: $time hours for $distance km")
    }

    fun compareFlightCapabilities(birds: Array<Bird>, testDistance: Double) {
        birds.forEach { bird ->
            simulateFlight(bird, testDistance)
            println("---")
        }
    }
}

