    fun getSpecies(): String = species
}

interface FlyingBird : Bird {
    val wingSpan: Double
    fun fly()
    fun getFlightSpeed(): Double
}

class Eagle(wingSpan: Double) : FlyingBird {
    override val wingSpan: Double = wingSpan

    override fun fly() {
        println("Eagle soaring at high altitude with ${wingSpan}m wingspan")
    }

    override fun getFlightSpeed(): Double {
        return 80.0 + (wingSpan * 10)
    }
}

class Hummingbird(wingSpan: Double) : FlyingBird {
    override val wingSpan: Double = wingSpan

    override fun fly() {
        println("Hummingbird hovering with rapid wing beats")
    }

    override fun getFlightSpeed(): Double {
        return 50.0
    }
}

class Ostrich(private val runningSpeed: Double) : Bird("Ostrich") {
