}

public class CorporateBond : FinancialInstrument
{
    private readonly decimal _couponRate;
    private readonly DateTime _maturityDate;
    private readonly string _creditRating;
    private readonly List<DateTime> _couponPaymentDates;
    private readonly ConcurrentDictionary<string, decimal> _bondMetrics;

    public CorporateBond(string instrumentId, decimal faceValue, decimal couponRate, 
                        DateTime maturityDate, string creditRating, ILogger logger) 
        : base(instrumentId, faceValue, logger)
    {
        _couponRate = couponRate;
        _maturityDate = maturityDate;
        _creditRating = creditRating;
        _couponPaymentDates = GenerateCouponDates();
        _bondMetrics = new ConcurrentDictionary<string, decimal>();
    }

    public override async Task<decimal> CalculateCurrentValueAsync(DateTime asOfDate)
    {
        if (asOfDate > _maturityDate)
        {
            return 0m;
        }

        var presentValue = FaceValue;

        foreach (var paymentDate in _couponPaymentDates.Where(d => d > asOfDate))
        {
            var couponPayment = FaceValue * _couponRate;
            presentValue += couponPayment;
        }

        await Task.Delay(100);
        return presentValue;
    }

    public override async Task<bool> ExecuteTransactionAsync(decimal amount, string counterparty)
    {
        if (amount != FaceValue)
        {
            throw new ArgumentException("Corporate bonds must be traded at face value");
        }

        if (!ValidateCounterparty(counterparty))
        {
            return false;
        }

        Logger.LogInformation($"Executing bond transaction: {InstrumentId}");
        await Task.Delay(1000);
        return true;
    }

    public override List<string> GetRequiredDocuments()
    {
        return new List<string>
        {
            "Bond Certificate",
            "Credit Rating Report",
            "Indenture Agreement"
        };
    }

    public override bool ValidateInstrument()
    {
        return !string.IsNullOrEmpty(_creditRating) &&
               _maturityDate > DateTime.Now &&
               _couponRate > 0;
    }

    public override Dictionary<string, object> GetRiskMetrics()
    {
        return new Dictionary<string, object>
        {
            ["creditRisk"] = _creditRating,
            ["duration"] = CalculateDuration(),
            ["yieldToMaturity"] = CalculateYTM(),
            ["interestRateRisk"] = RiskLevel.Medium
        };
    }

    private List<DateTime> GenerateCouponDates()
    {
        var dates = new List<DateTime>();
        var current = CreationTime.AddMonths(6);

        while (current < _maturityDate)
        {
            dates.Add(current);
            current = current.AddMonths(6);
        }

        return dates;
    }

    private bool ValidateCounterparty(string counterparty)
    {
        return !string.IsNullOrEmpty(counterparty) && counterparty.Length > 5;
    }

    private decimal CalculateDuration()
    {
        return _bondMetrics.GetOrAdd("duration", _ => 2.5m);
    }

    private decimal CalculateYTM()
    {
        return _bondMetrics.GetOrAdd("ytm", _ => _couponRate * 1.1m);
    }
}

