class PaymentMethodSelector {
    private val processors = mapOf(
        "credit_card" to CreditCardProcessor(),
        "paypal" to PayPalProcessor()
    )

    fun getProcessor(paymentMethod: String): PaymentProcessor? {
        return processors[paymentMethod]
    }
}

