using System;
using System.Threading.Tasks;

public abstract class PaymentProcessor
{
    public abstract Task ProcessPaymentAsync(double amount);
    public abstract bool ValidatePayment(double amount);
    public abstract string GetPaymentMethod();
}

public class CreditCardProcessor : PaymentProcessor
{
    private readonly string cardNumber;
    private readonly string expiryDate;

    public CreditCardProcessor(string cardNumber, string expiryDate)
    {
        this.cardNumber = cardNumber;
        this.expiryDate = expiryDate;
    }

    public override async Task ProcessPaymentAsync(double amount)
    {
        if (!ValidatePayment(amount))
        {
            throw new InvalidOperationException("Invalid credit card payment");
        }
        Console.WriteLine($"Processing credit card payment of ${amount}");
        await Task.Delay(2000);
    }

    public override bool ValidatePayment(double amount)
    {
        return !string.IsNullOrEmpty(cardNumber) && cardNumber.Length == 16 && amount > 0;
    }

    public override string GetPaymentMethod()
    {
        return "Credit Card";
    }
}

public class BankTransferProcessor : PaymentProcessor
{
    private readonly string accountNumber;
    private readonly string routingNumber;

    public BankTransferProcessor(string accountNumber, string routingNumber)
    {
        this.accountNumber = accountNumber;
        this.routingNumber = routingNumber;
    }

    public override async Task ProcessPaymentAsync(double amount)
    {
        if (amount < 100)
        {
            throw new ArgumentException("Bank transfers must be at least $100");
        }
        if (!ValidatePayment(amount))
        {
            throw new InvalidOperationException("Invalid bank transfer");
        }
        Console.WriteLine($"Processing bank transfer of ${amount}");
        await Task.CompletedTask;
    }

    public override bool ValidatePayment(double amount)
    {
        return !string.IsNullOrEmpty(accountNumber) && !string.IsNullOrEmpty(routingNumber) && amount >= 100;
    }

    public override string GetPaymentMethod()
    {
        return "Bank Transfer";
    }
}

public class DigitalWalletProcessor : PaymentProcessor
{
    private readonly string walletId;
    private double balance;

    public DigitalWalletProcessor(string walletId, double balance)
    {
        this.walletId = walletId;
        this.balance = balance;
    }

    public override async Task ProcessPaymentAsync(double amount)
    {
        if (balance < amount)
        {
            throw new InsufficientFundsException("Insufficient wallet balance");
        }
        if (!ValidatePayment(amount))
        {
            throw new InvalidOperationException("Invalid wallet payment");
        }
        balance -= amount;
        Console.WriteLine($"Processing wallet payment of ${amount}. Remaining balance: ${balance}");
        await Task.CompletedTask;
    }

    public override bool ValidatePayment(double amount)
    {
        return !string.IsNullOrEmpty(walletId) && balance >= amount && amount > 0;
    }

    public override string GetPaymentMethod()
    {
        return "Digital Wallet";
    }

    public double GetBalance()
    {
        return balance;
    }
}

