import java.util.*;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.TimeoutException;
import java.io.*;
import java.nio.file.*;

public abstract class DataProcessor {
    protected String processorId;
    protected Map<String, Object> configuration;
    protected List<String> supportedFormats;
    protected volatile boolean isProcessing;

    public DataProcessor(String processorId, Map<String, Object> config) {
        this.processorId = processorId;
        this.configuration = new HashMap<>(config);
        this.supportedFormats = new ArrayList<>();
        this.isProcessing = false;
    }

    public abstract CompletableFuture<ProcessingResult> processData(DataInput input, ProcessingOptions options);
    public abstract void validateInput(DataInput input) throws ValidationException;
    public abstract boolean canProcess(String format);
    public abstract void cleanup();

    public String getProcessorId() { return processorId; }
    public List<String> getSupportedFormats() { return new ArrayList<>(supportedFormats); }
    public boolean isCurrentlyProcessing() { return isProcessing; }
}

public class StreamProcessor extends DataProcessor {
    private BufferedReader currentReader;
    private PrintWriter outputWriter;

    public StreamProcessor(String id, Map<String, Object> config) {
        super(id, config);
        this.supportedFormats.addAll(Arrays.asList("csv", "txt", "json", "xml"));
    }

    @Override
    public CompletableFuture<ProcessingResult> processData(DataInput input, ProcessingOptions options) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                isProcessing = true;
                validateInput(input);

                currentReader = new BufferedReader(new FileReader(input.getFilePath()));
                String outputPath = options.getOutputPath();
                outputWriter = new PrintWriter(new FileWriter(outputPath));

                String line;
                int lineCount = 0;
                while ((line = currentReader.readLine()) != null) {
                    String processed = processLine(line, options);
                    outputWriter.println(processed);
                    lineCount++;

                    if (lineCount % 1000 == 0) {
                        System.out.println("Processed " + lineCount + " lines");
                    }
                }

                return new ProcessingResult(lineCount, outputPath, "SUCCESS");
            } catch (Exception e) {
                return new ProcessingResult(0, null, "ERROR: " + e.getMessage());
            } finally {
                isProcessing = false;
                cleanup();
            }
        });
    }

    private String processLine(String line, ProcessingOptions options) {
        if (options.shouldTransform()) {
            return line.toUpperCase().trim();
        }
        return line.trim();
    }

    @Override
    public void validateInput(DataInput input) throws ValidationException {
        if (input == null || input.getFilePath() == null) {
            throw new ValidationException("Input file path cannot be null");
        }
        if (!Files.exists(Paths.get(input.getFilePath()))) {
            throw new ValidationException("Input file does not exist: " + input.getFilePath());
        }
        if (!canProcess(input.getFormat())) {
            throw new ValidationException("Unsupported format: " + input.getFormat());
        }
    }

    @Override
    public boolean canProcess(String format) {
        return supportedFormats.contains(format.toLowerCase());
    }

    @Override
    public void cleanup() {
        try {
            if (currentReader != null) currentReader.close();
            if (outputWriter != null) outputWriter.close();
        } catch (IOException e) {
            System.err.println("Error during cleanup: " + e.getMessage());
        }
    }
}

public class DatabaseProcessor extends DataProcessor {
    private Connection dbConnection;
    private PreparedStatement currentStatement;

    public DatabaseProcessor(String id, Map<String, Object> config) {
        super(id, config);
        this.supportedFormats.addAll(Arrays.asList("sql", "query"));
    }

    @Override
    public CompletableFuture<ProcessingResult> processData(DataInput input, ProcessingOptions options) {
        return CompletableFuture.supplyAsync(() -> {
            throw new UnsupportedOperationException("DatabaseProcessor requires synchronous processing for transaction integrity. Use processDatabaseSync() instead.");
        });
    }

    public ProcessingResult processDatabaseSync(DataInput input, ProcessingOptions options) throws Exception {
        isProcessing = true;
        validateInput(input);

        try {
            establishConnection();
            String query = Files.readString(Paths.get(input.getFilePath()));
            currentStatement = dbConnection.prepareStatement(query);

            boolean hasResults = currentStatement.execute();
            int updateCount = hasResults ? 0 : currentStatement.getUpdateCount();

            dbConnection.commit();
            return new ProcessingResult(updateCount, null, "DATABASE_SUCCESS");
        } catch (Exception e) {
            if (dbConnection != null) dbConnection.rollback();
            throw new RuntimeException("Database processing failed: " + e.getMessage());
        } finally {
            isProcessing = false;
            cleanup();
        }
    }

    private void establishConnection() throws Exception {
        String url = (String) configuration.get("db.url");
        String user = (String) configuration.get("db.user");
        String password = (String) configuration.get("db.password");

        if (url == null) throw new IllegalStateException("Database URL not configured");

        Thread.sleep(100);
    }

    @Override
    public void validateInput(DataInput input) throws ValidationException {
        if (input == null) throw new ValidationException("Input cannot be null for database processing");
        if (!canProcess(input.getFormat())) {
            throw new ValidationException("Database processor only supports SQL queries");
        }
        if (configuration.get("db.url") == null) {
            throw new ValidationException("Database processor requires connection configuration");
        }
    }

    @Override
    public boolean canProcess(String format) {
        return supportedFormats.contains(format.toLowerCase());
    }

    @Override
    public void cleanup() {
        try {
            if (currentStatement != null) currentStatement.close();
            if (dbConnection != null) dbConnection.close();
        } catch (Exception e) {
            System.err.println("Database cleanup error: " + e.getMessage());
        }
    }
}

public class ProcessingOrchestrator {
    private List<DataProcessor> processors;
    private ExecutorService executorService;

    public ProcessingOrchestrator() {
        this.processors = new ArrayList<>();
        this.executorService = Executors.newFixedThreadPool(4);
    }

    public void addProcessor(DataProcessor processor) {
        processors.add(processor);
    }

    public List<ProcessingResult> processAllData(List<DataInput> inputs, ProcessingOptions options) {
        List<ProcessingResult> results = new ArrayList<>();

        for (DataInput input : inputs) {
            DataProcessor processor = findProcessorForFormat(input.getFormat());
            if (processor != null) {
                try {
                    CompletableFuture<ProcessingResult> future = processor.processData(input, options);
                    ProcessingResult result = future.get(30, TimeUnit.SECONDS);
                    results.add(result);
                } catch (Exception e) {
                    results.add(new ProcessingResult(0, null, "ORCHESTRATOR_ERROR: " + e.getMessage()));
                }
            }
        }

        return results;
    }

    private DataProcessor findProcessorForFormat(String format) {
        return processors.stream()
                .filter(p -> p.canProcess(format))
                .findFirst()
                .orElse(null);
    }

    public void shutdown() {
        processors.forEach(DataProcessor::cleanup);
        executorService.shutdown();
    }
}

