public class StreamProcessor : DataProcessor
{
    public override bool CanProcess(string formatType)
    {
        return formatType == "stream";
    }

    public override async Task<ProcessingResult> ProcessDataAsync(DataInput inputData, ProcessingOptions options)
    {
        if (!CanProcess(inputData.FormatType))
            throw new InvalidOperationException("Invalid format type for StreamProcessor");

        var startTime = DateTime.UtcNow;
        try
        {
            using var stream = File.OpenRead(inputData.FilePath);
            // Simulate processing the stream
            await Task.Delay(1000); // Simulate async work

            return new ProcessingResult
            {
                RecordsProcessed = 1,
                OutputLocation = null,
                StatusMessage = "STREAM_SUCCESS",
                ProcessingTimeMs = (long)(DateTime.UtcNow - startTime).TotalMilliseconds
            };
        }
        catch (Exception ex)
        {
            return new ProcessingResult
            {
                RecordsProcessed = 0,
                OutputLocation = null,
                StatusMessage = $"STREAM_ERROR: {ex.Message}",
                ProcessingTimeMs = (long)(DateTime.UtcNow - startTime).TotalMilliseconds,
                Errors = new List<string> { ex.Message }
            };
        }
    }

    public override void ValidateInput(DataInput inputData)
    {
        if (string.IsNullOrWhiteSpace(inputData?.FilePath))
            throw new ValidationException("Stream processor requires valid file path");

        if (!CanProcess(inputData.FormatType))
            throw new ValidationException($"Stream processor cannot handle format: {inputData.FormatType}");

        if (!File.Exists(inputData.FilePath))
            throw new ValidationException($"File not found: {inputData.FilePath}");
    }
}

