from abc import ABC, abstractmethod
from datetime import datetime
from threading import Lock
from typing import List, Dict, Optional
import re

class DocumentPermissions:
    def __init__(self, can_read: bool, can_write: bool, can_print: bool, can_export: bool, can_backup: bool):
        self.can_read = can_read
        self.can_write = can_write
        self.can_print = can_print
        self.can_export = can_export
        self.can_backup = can_backup

class DocumentStatistics:
    def __init__(self, word_count: int, character_count: int, page_count: int, file_size_kb: float):
        self.word_count = word_count
        self.character_count = character_count
        self.page_count = page_count
        self.file_size_kb = file_size_kb

class DocumentException(Exception):
    pass

class Document(ABC):
    def __init__(self, content: str, format_type: str, permissions: DocumentPermissions):
        self.content = content
        self.format = format_type
        self.encrypted = False
        self.created_at = datetime.now()
        self.last_modified = datetime.now()
        self.metadata: Dict[str, str] = {}
        self.revision_history: List[str] = []
        self.permissions = permissions
        self.revision_history.append(f"Document created at {self.created_at}")

    @abstractmethod
    def save(self) -> None:
        pass

    @abstractmethod
    def print_doc(self) -> None:
        pass

    @abstractmethod
    def export(self, target_format: str) -> None:
        pass

    @abstractmethod
    def compress(self) -> None:
        pass

    @abstractmethod
    def backup(self, location: str) -> None:
        pass

    @abstractmethod
    def get_statistics(self) -> DocumentStatistics:
        pass

    @abstractmethod
    def add_watermark(self, watermark_text: str) -> None:
        pass

    @abstractmethod
    def digitally_sign(self, certificate: str) -> None:
        pass

    def _update_last_modified(self):
        self.last_modified = datetime.now()
        self.revision_history.append(f"Document modified at {self.last_modified}")

class EnterpriseWordDocument(Document):
    def __init__(self, content: str, author: str, department: str):
        super().__init__(content, "DOCX", DocumentPermissions(True, True, True, True, True))
        self.author = author
        self.department = department
        self.collaborators: List[str] = []
        self.document_lock = Lock()
        self.is_being_edited = False
        self.metadata["author"] = author
        self.metadata["department"] = department

    def save(self) -> None:
        with self.document_lock:
            if not self.permissions.can_write:
                raise DocumentException("No write permission for this document")
            print(f"Saving enterprise Word document by {self.author} in {self.department}")
            self._update_last_modified()
            self.is_being_edited = False

    def print_doc(self) -> None:
        if not self.permissions.can_print:
            raise DocumentException("No print permission for this document")
        print("Printing enterprise Word document with watermark")

    def export(self, target_format: str) -> None:
        if not self.permissions.can_export:
            raise DocumentException("No export permission for this document")
        print(f"Exporting enterprise Word document to {target_format} with audit trail")

    def compress(self) -> None:
        print("Compressing enterprise Word document using advanced algorithms")
        self.content = re.sub(r'\s+', ' ', self.content).strip()
        self._update_last_modified()

    def backup(self, location: str) -> None:
        if not self.permissions.can_backup:
            raise DocumentException("No backup permission for this document")
        print(f"Creating encrypted backup of enterprise document at {location}")

    def get_statistics(self) -> DocumentStatistics:
        word_count = len(self.content.split())
        char_count = len(self.content)
        page_count = max(1, word_count // 250)
        file_size = len(self.content) * 0.001
        return DocumentStatistics(word_count, char_count, page_count, file_size)

    def add_watermark(self, watermark_text: str) -> None:
        print(f"Adding enterprise watermark: {watermark_text}")
        self._update_last_modified()

    def digitally_sign(self, certificate: str) -> None:
        print(f"Digitally signing document with certificate: {certificate}")
        self.encrypted = True
        self._update_last_modified()

class LegacyDocument(Document):
    def __init__(self, content: str, original_system: str):
        super().__init__(content, "LEGACY", DocumentPermissions(True, False, False, False, False))
        self.original_system = original_system
        self.conversion_required = True
        self.conversion_errors: List[str] = []
        self.metadata["original_system"] = original_system
        self.metadata["migration_status"] = "pending"

    def save(self) -> None:
        raise DocumentException("Legacy documents are read-only and cannot be saved in modern format")

    def print_doc(self) -> None:
        raise DocumentException("Legacy documents cannot be printed without conversion")

    def export(self, target_format: str) -> None:
        raise DocumentException("Legacy documents cannot be exported without manual conversion")

    def compress(self) -> None:
        raise DocumentException("Legacy documents use proprietary compression that cannot be modified")

    def backup(self, location: str) -> None:
        raise DocumentException("Legacy documents require specialized backup procedures")

    def get_statistics(self) -> DocumentStatistics:
        return DocumentStatistics(0, len(self.content), 1, len(self.content) * 0.002)

    def add_watermark(self, watermark_text: str) -> None:
        raise DocumentException("Cannot add watermark to legacy document format")

    def digitally_sign(self, certificate: str) -> None:
        raise DocumentException("Digital signatures not supported for legacy document format")

    def attempt_conversion(self):
        print(f"Attempting to convert legacy document from {self.original_system}")
        self.conversion_errors.append("Formatting may be lost during conversion")
        self.conversion_errors.append("Some features not supported in modern format")

