    public abstract DocumentStatistics GetStatistics();
    public abstract DocumentOperationResult AddWatermark(string watermarkText);
    public abstract DocumentOperationResult DigitallySign(string certificate);

    protected void UpdateLastModified()
    {
        // Logic to update last modified time
        Console.WriteLine("Document metadata updated with new timestamp.");
    }

    public virtual void StartEditing()
    {
        documentLock.EnterWriteLock();
        try
        {
            isBeingEdited = true;
            Console.WriteLine($"Document is now being edited");
        }
        finally
        {
            documentLock.ExitWriteLock();
        }
    }
}

public class DocumentPermissions
{
    public bool CanRead { get; set; }
    public bool CanWrite { get; set; }
    public bool CanPrint { get; set; }
    public bool CanExport { get; set; }
    public bool CanBackup { get; set; }

    public DocumentPermissions(bool canRead, bool canWrite, bool canPrint, bool canExport, bool canBackup)
    {
        CanRead = canRead;
        CanWrite = canWrite;
        CanPrint = canPrint;
        CanExport = canExport;
        CanBackup = canBackup;
    }
}

public class EnterpriseWordDocument : Document
{
    private string author;
    private string department;

    public EnterpriseWordDocument(string content, string author, string department)
        : base(content, "WORD", new DocumentPermissions(true, true, true, true, true))
    {
        this.author = author;
        this.department = department;
        Metadata["author"] = author;
        Metadata["department"] = department;
    }

    public override DocumentOperationResult Save()
    {
        documentLock.EnterWriteLock();
        try
        {
            if (!Permissions.CanWrite)
                return new DocumentOperationResult(false, "No write permission for this document");

            Console.WriteLine($"Saving enterprise Word document by {author} in {department}");
            UpdateLastModified();
            isBeingEdited = false;
            return new DocumentOperationResult(true, "Document saved successfully");
        }
        finally
        {
            documentLock.ExitWriteLock();
        }
    }

    public override DocumentOperationResult Print()
    {
        if (!Permissions.CanPrint)
            return new DocumentOperationResult(false, "No print permission for this document");

        Console.WriteLine("Printing enterprise Word document with watermark");
        return new DocumentOperationResult(true, "Document printed successfully");
    }

    public override DocumentOperationResult Export(string targetFormat)
    {
        if (!Permissions.CanExport)
            return new DocumentOperationResult(false, "No export permission for this document");

        Console.WriteLine($"Exporting enterprise Word document to {targetFormat} with audit trail");
        return new DocumentOperationResult(true, $"Document exported to {targetFormat}");
    }

    public override DocumentOperationResult Compress()
    {
        Console.WriteLine("Compressing enterprise Word document using advanced algorithms");
        Content = Regex.Replace(Content, @"\s+", " ").Trim();
        UpdateLastModified();
        return new DocumentOperationResult(true, "Document compressed successfully");
    }

    public override DocumentOperationResult Backup(string location)
    {
        if (!Permissions.CanBackup)
            return new DocumentOperationResult(false, "No backup permission for this document");

        Console.WriteLine($"Creating encrypted backup of enterprise document at {location}");
        return new DocumentOperationResult(true, $"Backup created at {location}");
    }

    public override DocumentStatistics GetStatistics()
    {
        var wordCount = Content.Split(new char[] { ' ', '\t', '\n', '\r' }, StringSplitOptions.RemoveEmptyEntries).Length;
        var charCount = Content.Length;
        var pageCount = Math.Max(1, wordCount / 250);
        var fileSize = Content.Length * 0.001;
        return new DocumentStatistics(wordCount, charCount, pageCount, fileSize);
    }

    public override DocumentOperationResult AddWatermark(string watermarkText)
    {
        Console.WriteLine($"Adding watermark '{watermarkText}' to the document");
        UpdateLastModified();
        return new DocumentOperationResult(true, "Watermark added successfully");
    }

    public override DocumentOperationResult DigitallySign(string certificate)
    {
        Console.WriteLine($"Digitally signing document with certificate: {certificate}");
        UpdateLastModified();
        return new DocumentOperationResult(true, "Document signed successfully");
    }
}

