import java.util.*;
import java.text.SimpleDateFormat;
import java.text.DecimalFormat;
import java.util.stream.Collectors;

public class SalesData {
    private List<Sale> sales;
    private String companyName;
    private String reportingPeriod;

    public SalesData(List<Sale> sales, String companyName, String reportingPeriod) {
        this.sales = sales;
        this.companyName = companyName;
        this.reportingPeriod = reportingPeriod;
    }

    public List<Sale> getSales() {
        return sales;
    }

    public String getCompanyName() {
        return companyName;
    }

    public String getReportingPeriod() {
        return reportingPeriod;
    }

    public void addSale(Sale sale) {
        this.sales.add(sale);
    }

    public List<Sale> getSalesByRegion(String region) {
        return sales.stream()
                .filter(sale -> sale.getRegion().equals(region))
                .collect(Collectors.toList());
    }
}

public class Sale {
    private String productName;
    private String productCategory;
    private double amount;
    private int quantity;
    private Date date;
    private String region;
    private String salesPerson;
    private String customerId;
    private double discount;

    public Sale(String productName, String productCategory, double amount, int quantity, 
                Date date, String region, String salesPerson, String customerId, double discount) {
        this.productName = productName;
        this.productCategory = productCategory;
        this.amount = amount;
        this.quantity = quantity;
        this.date = date;
        this.region = region;
        this.salesPerson = salesPerson;
        this.customerId = customerId;
        this.discount = discount;
    }

    public String getProductName() {
        return productName;
    }

    public String getProductCategory() {
        return productCategory;
    }

    public double getAmount() {
        return amount;
    }

    public int getQuantity() {
        return quantity;
    }

    public Date getDate() {
        return date;
    }

    public String getRegion() {
        return region;
    }

    public String getSalesPerson() {
        return salesPerson;
    }

    public String getCustomerId() {
        return customerId;
    }

    public double getDiscount() {
        return discount;
    }

    public double getNetAmount() {
        return amount - discount;
    }
}

public class ReportGenerator {
    private DecimalFormat currencyFormat = new DecimalFormat("$#,##0.00");
    private SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");

    public String generateReport(String reportType, SalesData data) {
        StringBuilder report = new StringBuilder();

        // Add header
        report.append("===================================\n");
        report.append("    ").append(data.getCompanyName()).append("\n");
        report.append("  Reporting Period: ").append(data.getReportingPeriod()).append("\n");
        report.append("===================================\n\n");

        if (reportType.equals("daily")) {
            report.append("DAILY SALES REPORT\n");
            report.append("===================\n\n");

            Map<String, Double> dailySales = new HashMap<>();
            Map<String, Integer> dailyQuantities = new HashMap<>();

            for (Sale sale : data.getSales()) {
                String dateStr = dateFormat.format(sale.getDate());
                dailySales.put(dateStr, dailySales.getOrDefault(dateStr, 0.0) + sale.getNetAmount());
                dailyQuantities.put(dateStr, dailyQuantities.getOrDefault(dateStr, 0) + sale.getQuantity());
            }

            for (Map.Entry<String, Double> entry : dailySales.entrySet()) {
                String date = entry.getKey();
                report.append("Date: ").append(date)
                      .append(", Total Sales: ").append(currencyFormat.format(entry.getValue()))
                      .append(", Units Sold: ").append(dailyQuantities.get(date))
                      .append("\n");
            }

            double total = dailySales.values().stream().mapToDouble(Double::doubleValue).sum();
            int totalUnits = dailyQuantities.values().stream().mapToInt(Integer::intValue).sum();
            report.append("\nGrand Total: ").append(currencyFormat.format(total));
            report.append("\nTotal Units: ").append(totalUnits);
            report.append("\nAverage Daily Sales: ").append(currencyFormat.format(total / dailySales.size()));

        } else if (reportType.equals("product")) {
            report.append("PRODUCT SALES REPORT\n");
            report.append("====================\n\n");

            Map<String, Double> productSales = new HashMap<>();
            Map<String, Integer> productQuantities = new HashMap<>();
            Map<String, String> productCategories = new HashMap<>();

            for (Sale sale : data.getSales()) {
                String product = sale.getProductName();
                productSales.put(product, productSales.getOrDefault(product, 0.0) + sale.getNetAmount());
                productQuantities.put(product, productQuantities.getOrDefault(product, 0) + sale.getQuantity());
                productCategories.put(product, sale.getProductCategory());
            }

            // Sort by sales amount descending
            List<Map.Entry<String, Double>> sortedProducts = new ArrayList<>(productSales.entrySet());
            sortedProducts.sort(Map.Entry.<String, Double>comparingByValue().reversed());

            for (Map.Entry<String, Double> entry : sortedProducts) {
                String product = entry.getKey();
                report.append("Product: ").append(product)
                      .append(" (Category: ").append(productCategories.get(product)).append(")")
                      .append("\n  Sales: ").append(currencyFormat.format(entry.getValue()))
                      .append(", Units: ").append(productQuantities.get(product))
                      .append(", Avg Price: ").append(currencyFormat.format(entry.getValue() / productQuantities.get(product)))
                      .append("\n\n");
            }

            double total = productSales.values().stream().mapToDouble(Double::doubleValue).sum();
            report.append("Total Product Sales: ").append(currencyFormat.format(total));

        } else if (reportType.equals("regional")) {
            report.append("REGIONAL SALES REPORT\n");
            report.append("=====================\n\n");

            Map<String, Double> regionalSales = new HashMap<>();
            Map<String, Set<String>> regionalCustomers = new HashMap<>();
            Map<String, Set<String>> regionalSalespeople = new HashMap<>();

            for (Sale sale : data.getSales()) {
                String region = sale.getRegion();
                regionalSales.put(region, regionalSales.getOrDefault(region, 0.0) + sale.getNetAmount());

                regionalCustomers.computeIfAbsent(region, k -> new HashSet<>()).add(sale.getCustomerId());
                regionalSalespeople.computeIfAbsent(region, k -> new HashSet<>()).add(sale.getSalesPerson());
            }

            for (Map.Entry<String, Double> entry : regionalSales.entrySet()) {
                String region = entry.getKey();
                report.append("Region: ").append(region)
                      .append("\n  Total Sales: ").append(currencyFormat.format(entry.getValue()))
                      .append("\n  Unique Customers: ").append(regionalCustomers.get(region).size())
                      .append("\n  Sales Team Size: ").append(regionalSalespeople.get(region).size())
                      .append("\n  Avg Sales per Customer: ")
                      .append(currencyFormat.format(entry.getValue() / regionalCustomers.get(region).size()))
                      .append("\n\n");
            }

            double total = regionalSales.values().stream().mapToDouble(Double::doubleValue).sum();
            report.append("Total Regional Sales: ").append(currencyFormat.format(total));

        } else if (reportType.equals("salesperson")) {
            report.append("SALESPERSON PERFORMANCE REPORT\n");
            report.append("==============================\n\n");

            Map<String, Double> salespersonSales = new HashMap<>();
            Map<String, Set<String>> salespersonCustomers = new HashMap<>();
            Map<String, String> salespersonRegions = new HashMap<>();

            for (Sale sale : data.getSales()) {
                String salesperson = sale.getSalesPerson();
                salespersonSales.put(salesperson, salespersonSales.getOrDefault(salesperson, 0.0) + sale.getNetAmount());
                salespersonCustomers.computeIfAbsent(salesperson, k -> new HashSet<>()).add(sale.getCustomerId());
                salespersonRegions.put(salesperson, sale.getRegion());
            }

            // Sort by performance
            List<Map.Entry<String, Double>> sortedSalespeople = new ArrayList<>(salespersonSales.entrySet());
            sortedSalespeople.sort(Map.Entry.<String, Double>comparingByValue().reversed());

            int rank = 1;
            for (Map.Entry<String, Double> entry : sortedSalespeople) {
                String salesperson = entry.getKey();
                report.append("#").append(rank++).append(" ").append(salesperson)
                      .append(" (Region: ").append(salespersonRegions.get(salesperson)).append(")")
                      .append("\n  Total Sales: ").append(currencyFormat.format(entry.getValue()))
                      .append("\n  Customers Served: ").append(salespersonCustomers.get(salesperson).size())
                      .append("\n  Avg Sale per Customer: ")
                      .append(currencyFormat.format(entry.getValue() / salespersonCustomers.get(salesperson).size()))
                      .append("\n\n");
            }

        } else if (reportType.equals("category")) {
            report.append("CATEGORY ANALYSIS REPORT\n");
            report.append("========================\n\n");

            Map<String, Double> categorySales = new HashMap<>();
            Map<String, Integer> categoryQuantities = new HashMap<>();
            Map<String, Set<String>> categoryProducts = new HashMap<>();

            for (Sale sale : data.getSales()) {
                String category = sale.getProductCategory();
                categorySales.put(category, categorySales.getOrDefault(category, 0.0) + sale.getNetAmount());
                categoryQuantities.put(category, categoryQuantities.getOrDefault(category, 0) + sale.getQuantity());
                categoryProducts.computeIfAbsent(category, k -> new HashSet<>()).add(sale.getProductName());
            }

            double totalSales = categorySales.values().stream().mapToDouble(Double::doubleValue).sum();

            for (Map.Entry<String, Double> entry : categorySales.entrySet()) {
                String category = entry.getKey();
                double categoryTotal = entry.getValue();
                double percentage = (categoryTotal / totalSales) * 100;

                report.append("Category: ").append(category)
                      .append("\n  Sales: ").append(currencyFormat.format(categoryTotal))
                      .append(" (").append(String.format("%.1f", percentage)).append("% of total)")
                      .append("\n  Units Sold: ").append(categoryQuantities.get(category))
                      .append("\n  Product Variety: ").append(categoryProducts.get(category).size()).append(" products")
                      .append("\n\n");
            }
        }

        // Add footer
        report.append("\n\n===================================\n");
        report.append("Report Generated: ").append(new SimpleDateFormat("yyyy-MM-dd HH:mm:ss").format(new Date()));
        report.append("\n===================================\n");

        return report.toString();
    }

    public String generateSummaryReport(SalesData data) {
        StringBuilder summary = new StringBuilder();
        summary.append("EXECUTIVE SUMMARY\n");
        summary.append("=================\n\n");

        double totalSales = data.getSales().stream().mapToDouble(Sale::getNetAmount).sum();
        int totalTransactions = data.getSales().size();
        double avgTransaction = totalSales / totalTransactions;

        Set<String> uniqueCustomers = data.getSales().stream()
                .map(Sale::getCustomerId)
                .collect(Collectors.toSet());

        summary.append("Total Sales: ").append(currencyFormat.format(totalSales)).append("\n");
        summary.append("Total Transactions: ").append(totalTransactions).append("\n");
        summary.append("Average Transaction: ").append(currencyFormat.format(avgTransaction)).append("\n");
        summary.append("Unique Customers: ").append(uniqueCustomers.size()).append("\n");

        return summary.toString();
    }
}

